// Code (Function) Node Script - Focused on Input Debugging
const inputItem = items[0]; // Get the whole item, not just .json yet

// Log the entire incoming item to see its structure, including binary properties if any
console.log("Full Input Item to Code Node:", JSON.stringify(inputItem, null, 2));

const itemJson = inputItem.json; // Now get the JSON part

let fileId;
let fileName;
let webContentLink;

if (typeof itemJson !== 'object' || itemJson === null) {
    console.error("Input item.json is not an object or is null. Input was:", JSON.stringify(inputItem, null, 2));
    fileName = "Unknown Video (Input not an object)";
} else {
    // Try to access properties directly, assuming Google Drive File Resource structure
    fileId = itemJson.id;
    fileName = itemJson.name;
    webContentLink = 'REDACTED_WEBHOOK_URL'; // Fallback to webViewLink

    console.log(`Attempting to extract: id='${fileId}', name='${fileName}', webContentLink='${webContentLink}'`);

    if (typeof fileName !== 'string' || fileName.trim() === "") {
        console.warn("fileName is missing or invalid in Code Node input. Actual item.json.name was:", itemJson.name, ". Setting to 'Unknown Video'.");
        fileName = "Unknown Video";
    } else {
        console.log("Successfully extracted fileName:", fileName);
    }

    if (typeof fileId !== 'string' || fileId.trim() === "") {
        console.warn("fileId is missing or invalid.");
        // fileId remains as extracted or undefined
    }

    if (typeof webContentLink !== 'string' || webContentLink.trim() === "") {
        console.warn("webContentLink (and webViewLink) is missing or invalid.");
        // webContentLink remains as extracted or undefined
    }
}

const outputData = {
  processedFileId: fileId,
  processedFileName: fileName,
  processedWebContentLink: webContentLink
};

console.log("Output from Code Node:", JSON.stringify(outputData, null, 2));

// Return structure expected by n8n
return [{ json: outputData }];

const responseData = items[0].json;

if (!responseData || !responseData.files || responseData.files.length === 0) {
  console.warn("No files found in the source Google Drive folder via API or API response was unexpected.");
  return [];
}

const filesArray = responseData.files;
const randomIndex = Math.floor(Math.random() * filesArray.length);
const randomFile = filesArray[randomIndex];

return [{ json: randomFile }];
