// Extract and format order data for invoice
const orderData = $input.first().json.body;

// Calculate totals
const lineItems = orderData.line_items.map(item => ({
  name: item.name,
  quantity: item.quantity,
  price: parseFloat(item.price),
  total: parseFloat(item.price) * item.quantity,
  sku: item.sku || 'N/A'
}));

const subtotal = lineItems.reduce((sum, item) => sum + item.total, 0);
const taxAmount = parseFloat(orderData.total_tax || 0);
const shippingAmount = parseFloat(orderData.shipping_lines?.[0]?.price || 0);
const total = parseFloat(orderData.total_price);

// Format invoice data
const invoiceData = {
  // Order info
  orderNumber: orderData.order_number,
  orderDate: new Date(orderData.created_at).toLocaleDateString(),

  // Customer info
  customer: {
    name: `${orderData.customer.first_name} ${orderData.customer.last_name}`,
    email: orderData.customer.email,
    phone: orderData.customer.phone || 'N/A'
  },

  // Billing address
  billingAddress: orderData.billing_address ? {
    address1: orderData.billing_address.address1,
    address2: orderData.billing_address.address2 || '',
    city: orderData.billing_address.city,
    province: orderData.billing_address.province,
    zip: orderData.billing_address.zip,
    country: orderData.billing_address.country
  } : null,

  // Shipping address
  shippingAddress: orderData.shipping_address ? {
    address1: orderData.shipping_address.address1,
    address2: orderData.shipping_address.address2 || '',
    city: orderData.shipping_address.city,
    province: orderData.shipping_address.province,
    zip: orderData.shipping_address.zip,
    country: orderData.shipping_address.country
  } : null,

  // Line items
  items: lineItems,

  // Totals
  subtotal: subtotal.toFixed(2),
  tax: taxAmount.toFixed(2),
  shipping: shippingAmount.toFixed(2),
  total: total.toFixed(2),
  currency: orderData.currency,

  // Additional info
  paymentMethod: orderData.payment_gateway_names?.[0] || 'Online Payment',
  notes: orderData.note || ''
};

return { invoiceData };

// Generate professional invoice HTML
const data = $input.first().json.invoiceData;

const html = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Invoice #${data.orderNumber}</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Arial', sans-serif;
            line-height: 1.6;
            color: #333;
            background: #f8f9fa;
        }

        .container {
            max-width: 800px;
            margin: 0 auto;
            background: white;
            padding: 40px;
            box-shadow: 0 0 20px rgba(0,0,0,0.1);
        }

        .header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 40px;
            padding-bottom: 20px;
            border-bottom: 3px solid #007bff;
        }

        .company-info {
            flex: 1;
        }

        .company-name {
            font-size: 28px;
            font-weight: bold;
            color: #007bff;
            margin-bottom: 5px;
        }

        .invoice-info {
            text-align: right;
        }

        .invoice-title {
            font-size: 36px;
            font-weight: bold;
            color: #333;
            margin-bottom: 10px;
        }

        .invoice-number {
            font-size: 18px;
            color: #666;
        }

        .details-section {
            display: flex;
            justify-content: space-between;
            margin-bottom: 40px;
            gap: 40px;
        }

        .detail-box {
            flex: 1;
            padding: 20px;
            background: #f8f9fa;
            border-radius: 8px;
        }

        .detail-title {
            font-weight: bold;
            margin-bottom: 15px;
            color: #007bff;
            font-size: 16px;
        }

        .detail-content {
            color: #555;
        }

        .items-table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 30px;
        }

        .items-table th,
        .items-table td {
            padding: 15px;
            text-align: left;
            border-bottom: 1px solid #ddd;
        }

        .items-table th {
            background-color: #007bff;
            color: white;
            font-weight: bold;
            text-transform: uppercase;
            font-size: 14px;
        }

        .items-table tr:hover {
            background-color: #f5f5f5;
        }

        .text-right {
            text-align: right;
        }

        .totals-section {
            margin-left: auto;
            width: 300px;
        }

        .totals-table {
            width: 100%;
            border-collapse: collapse;
        }

        .totals-table td {
            padding: 10px 0;
            border-bottom: 1px solid #eee;
        }

        .total-row {
            font-weight: bold;
            font-size: 18px;
            border-top: 2px solid #007bff;
            color: #007bff;
        }

        .footer {
            margin-top: 50px;
            padding-top: 20px;
            border-top: 1px solid #ddd;
            text-align: center;
            color: #666;
            font-size: 14px;
        }

        .thank-you {
            font-size: 18px;
            color: #007bff;
            margin-bottom: 10px;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <div class="company-info">
                <div class="company-name">Your Company Name</div>
                <div>123 Business Street</div>
                <div>City, State 12345</div>
                <div>Phone: (555) 123-4567</div>
                <div>Email: info@yourcompany.com</div>
            </div>
            <div class="invoice-info">
                <div class="invoice-title">INVOICE</div>
                <div class="invoice-number">#${data.orderNumber}</div>
                <div style="margin-top: 10px;">Date: ${data.orderDate}</div>
            </div>
        </div>

        <div class="details-section">
            <div class="detail-box">
                <div class="detail-title">Bill To:</div>
                <div class="detail-content">
                    <strong>${data.customer.name}</strong><br>
                    ${data.customer.email}<br>
                    ${data.customer.phone}<br><br>
                    ${data.billingAddress ? `
                        ${data.billingAddress.address1}<br>
                        ${data.billingAddress.address2 ? data.billingAddress.address2 + '<br>' : ''}
                        ${data.billingAddress.city}, ${data.billingAddress.province} ${data.billingAddress.zip}<br>
                        ${data.billingAddress.country}
                    ` : ''}
                </div>
            </div>

            ${data.shippingAddress ? `
            <div class="detail-box">
                <div class="detail-title">Ship To:</div>
                <div class="detail-content">
                    ${data.shippingAddress.address1}<br>
                    ${data.shippingAddress.address2 ? data.shippingAddress.address2 + '<br>' : ''}
                    ${data.shippingAddress.city}, ${data.shippingAddress.province} ${data.shippingAddress.zip}<br>
                    ${data.shippingAddress.country}
                </div>
            </div>
            ` : ''}

            <div class="detail-box">
                <div class="detail-title">Payment Info:</div>
                <div class="detail-content">
                    <strong>Method:</strong> ${data.paymentMethod}<br>
                    <strong>Currency:</strong> ${data.currency}<br>
                    <strong>Status:</strong> Paid
                </div>
            </div>
        </div>

        <table class="items-table">
            <thead>
                <tr>
                    <th>Item</th>
                    <th>SKU</th>
                    <th>Qty</th>
                    <th>Price</th>
                    <th>Total</th>
                </tr>
            </thead>
            <tbody>
                ${data.items.map(item => `
                <tr>
                    <td><strong>${item.name}</strong></td>
                    <td>${item.sku}</td>
                    <td>${item.quantity}</td>
                    <td>$${item.price.toFixed(2)}</td>
                    <td class="text-right">$${item.total.toFixed(2)}</td>
                </tr>
                `).join('')}
            </tbody>
        </table>

        <div class="totals-section">
            <table class="totals-table">
                <tr>
                    <td>Subtotal:</td>
                    <td class="text-right">$${data.subtotal}</td>
                </tr>
                ${parseFloat(data.shipping) > 0 ? `
                <tr>
                    <td>Shipping:</td>
                    <td class="text-right">$${data.shipping}</td>
                </tr>
                ` : ''}
                ${parseFloat(data.tax) > 0 ? `
                <tr>
                    <td>Tax:</td>
                    <td class="text-right">$${data.tax}</td>
                </tr>
                ` : ''}
                <tr class="total-row">
                    <td><strong>Total:</strong></td>
                    <td class="text-right"><strong>$${data.total}</strong></td>
                </tr>
            </table>
        </div>

        ${data.notes ? `
        <div style="margin-top: 30px; padding: 20px; background: #f8f9fa; border-radius: 8px;">
            <strong>Notes:</strong><br>
            ${data.notes}
        </div>
        ` : ''}

        <div class="footer">
            <div class="thank-you">Thank you for your business!</div>
            <div>This invoice was generated automatically on ${new Date().toLocaleDateString()}</div>
            <div style="margin-top: 10px;">For questions about this invoice, please contact us at info@yourcompany.com</div>
        </div>
    </div>
</body>
</html>
`;

return { html };

