const files = $('Slack Trigger').first().json.files || [];
const results = [];

for (const file of files) {
  const mimetype = file.mimetype || "";
  const fileType = mimetype.split('/').pop().toLowerCase();
  const fileName = file.name || file.title || "unknown";

  const isImage = ['jpg', 'jpeg', 'png', 'webp'].includes(fileType);
  const isDocument = ['pdf', 'doc', 'docx', 'txt', 'rtf', 'eml'].includes(fileType);
  const isEmail = fileType === 'eml' || fileName.endsWith('.eml');

  results.push({
    json: {
      ...file, // keep Slack file data like url_private_download
      fileType,
      mimetype,
      isImage,
      isDocument,
      isEmail,
    }
  });
}

return results;

// $input.all() gives you the 3 separate OCR result items
const inputItems = $input.all();

return inputItems.map((item, index) => {
  const parsedResults = item.json.ParsedResults || [];

  // Join all ParsedText per result into one string
  const mergedText = parsedResults
    .map(p => p.ParsedText || "")
    .join("\n\n")
    .trim();

  return {
    json: {
      index: index + 1,
      mergedParsedText: mergedText
    }
  };
});

// n8n Code node — Run Once for All Items
// Repair + Enforce schema + attach Slack file info
// Adds discount_percent support + derives discount_amount & stable amount_total
// + Partial payment normalization (amount_due, paid_amount, paid_date, receipt_no, payment_method)

function stripCodeFences(s) {
  return String(s || "")
    .replace(/```(?:json)?/gi, "")
    .replace(/```/g, "")
    .trim();
}

function findTopLevelJson(s) {
  const str = String(s || "");
  let start = -1, depth = 0;
  for (let i = 0; i < str.length; i++) {
    const ch = str[i];
    if (start === -1) {
      if (ch === "{" || ch === "[") { start = i; depth = 1; }
    } else {
      if (ch === "{" || ch === "[") depth++;
      if (ch === "}" || ch === "]") depth--;
      if (depth === 0) return str.slice(start, i + 1);
    }
  }
  return null;
}

function safeParseAny(x) {
  if (Array.isArray(x)) return x;
  if (x && typeof x === "object" && (x.invoice_no || x.vendor || x.line_items)) return x;

  let txt = "";
  if (typeof x === "string") txt = x;
  else if (typeof x?.text === "string") txt = x.text;
  else if (typeof x?.output === "string") txt = x.output;
  else if (typeof x?.response === "string") txt = x.response;
  else if (typeof x?.content === "string") txt = x.content;
  else if (typeof x?.message === "string") txt = x.message;
  else txt = JSON.stringify(x ?? "");

  txt = stripCodeFences(txt);

  try { return JSON.parse(txt); } catch {}
  const block = findTopLevelJson(txt);
  if (!block) throw new Error("No JSON found in LLM output");
  return JSON.parse(block);
}

// coercers
function num(v) {
  if (v === "" || v == null || (typeof v === "number" && !isFinite(v))) return 0;
  if (typeof v === "number") return v;
  let s = String(v).trim();
  if (!s) return 0;
  // keep digits , . -
  s = s.replace(/[^\d,.\-]/g, "");
  if (s.includes(",") && s.includes(".")) s = s.replace(/,/g, "");
  else if (s.includes(",") && !s.includes(".")) {
    if ((s.match(/,/g) || []).length === 1 && /\d,\d{1,2}$/.test(s)) s = s.replace(",", ".");
    else s = s.replace(/,/g, "");
  }
  const n = Number(s);
  return isFinite(n) ? n : 0;
}
const str = (v) => (v == null ? "" : String(v).trim());
const bool = (v) => v === true;
const two = (n) => Math.round((Number(n) || 0) * 100) / 100;

// parse percent from "49%", "49", 0.49, etc. → 0..1
function pct(v) {
  if (v == null || v === "") return 0;
  if (typeof v === "string" && v.includes("%")) return Math.min(Math.max(num(v) / 100, 0), 1);
  let n = num(v);
  if (n > 1) n = n / 100;
  if (n < 0) n = 0;
  if (n > 1) n = 1;
  return n;
}

function normalizeOne(obj, fileUrl) {
  const line_items = Array.isArray(obj?.line_items)
    ? obj.line_items.map(x => ({
        description: str(x?.description),
        qty: num(x?.qty),
        unit_price: num(x?.unit_price),
        amount: num(x?.amount)
      }))
    : [];

  // base fields from LLM
  const base = {
    invoice_no: str(obj?.invoice_no),
    vendor: str(obj?.vendor),
    issue_date: str(obj?.issue_date),
    due_date: str(obj?.due_date),
    currency: str(obj?.currency),
    subtotal: num(obj?.subtotal),
    tax_total: num(obj?.tax_total),

    // discounts (raw)
    discount_total: num(obj?.discount_total),      // fixed amount if present
    discount_percent: pct(obj?.discount_percent),  // normalized 0..1

    amount_total: num(obj?.amount_total),

    // payment/receipt extras (raw; normalized below)
    amount_due: num(obj?.amount_due),
    paid_amount: num(obj?.paid_amount),
    paid_date: str(obj?.paid_date),
    receipt_no: str(obj?.receipt_no),
    payment_method: str(obj?.payment_method),

    destination_account: str(obj?.destination_account),
    payment_ref: str(obj?.payment_ref),
    doc_says_paid: bool(obj?.doc_says_paid),
    status: str(obj?.status),
    notes: str(obj?.notes),
    source_file_id: str(obj?.source_file_id),
    source_file_name: str(obj?.source_file_name),
    source_file_url: str(obj?.source_file_url),
    ingestion_batch: str(obj?.ingestion_batch),
    line_items,
    attachments: Array.isArray(obj?.attachments) ? obj.attachments.map(str) : []
  };

  // ---- discount math ----
  const discount_amount =
    base.discount_total > 0
      ? two(base.discount_total)
      : two(base.subtotal * (base.discount_percent || 0));

  // compute fallback amount_total
  const calc_total = two(base.subtotal - discount_amount + base.tax_total);

  // choose stable amount_total for pipeline (prefer LLM if sane; else calc)
  const provided = base.amount_total;
  const useProvided =
    provided > 0 && Math.abs(provided - calc_total) <= Math.max(0.01, calc_total * 0.01);
  base.amount_total = useProvided ? two(provided) : calc_total;

  // Notion percent wants 0..1
  base.discount_percent_for_notion = +(base.discount_percent || 0).toFixed(4);
  base.discount_amount = discount_amount; // handy for dedup & debug
  base._amount_total_source = useProvided ? "provided" : "calculated";

  // ---- partial / payment normalization ----
  const at = Number(base.amount_total || 0);
  let due  = Number(base.amount_due || 0);
  let paid = Number(base.paid_amount || 0);

  // Derive missing pieces when amount_total is known
  if (at > 0) {
    if (paid > 0 && due <= 0) due = Math.max(0, two(at - paid));
    if (due > 0 && paid <= 0) paid = Math.max(0, two(at - due));
    // If doc says PAID and due is zero but paid missing, assume fully paid
    if (base.doc_says_paid && due === 0 && paid <= 0) paid = at;
    // Clamp impossible values
    if (paid < 0) paid = 0;
    if (due < 0) due = 0;
    if (paid > at) paid = at;
    if (due > at) due = at;
    if (paid + due > at) due = Math.max(0, two(at - paid));
  } else {
    // amount_total unknown — still clamp non-negatives
    if (paid < 0) paid = 0;
    if (due  < 0) due  = 0;
  }

  base.paid_amount = two(paid);
  base.amount_due  = two(due);

  // Receipt hints payload for later routing/Notion Receipt DB
  base.receipt_hints = {
    paid_amount: base.paid_amount,
    paid_date: base.paid_date,
    receipt_no: base.receipt_no,
    payment_method: base.payment_method
  };

  // Nudge status if inconsistent with amounts
  if (at > 0) {
    if (base.amount_due === 0 && (base.doc_says_paid || base.paid_amount >= at - 0.01)) {
      base.status = "Paid (Unverified)";
    } else if (base.paid_amount > 0 && base.amount_due > 0) {
      base.status = "Partially Paid";
    } else if (base.paid_amount === 0 && base.amount_due >= at - 0.01) {
      base.status = "Unpaid";
    }
  }

  // ---- Attach Slack file URL (if available) ----
  if (fileUrl) {
    let fileName = "", fileId = "";
    try {
      const u = new URL(fileUrl);
      const seg = u.pathname.split("/").filter(Boolean);
      fileName = decodeURIComponent(seg[seg.length - 1] || "");
      const m = u.pathname.match(/-(F[A-Z0-9]+)\//i);
      if (m) fileId = m[1];
    } catch {
      const m1 = fileUrl.match(/\/download\/([^?\/#]+)($|\?)/i);
      if (m1) fileName = decodeURIComponent(m1[1]);
      const m2 = fileUrl.match(/-(F[A-Z0-9]+)\//i);
      if (m2) fileId = m2[1];
    }

    base.source_file_url = fileUrl;
    if (!base.source_file_name) base.source_file_name = fileName;
    if (!base.source_file_id) base.source_file_id = fileId;

    const atSet = new Set(base.attachments.map(String));
    atSet.add(fileUrl);
    base.attachments = Array.from(atSet);
  }

  // Guard status values
  const allowedStatus = new Set([
    "Unpaid","Paid (Unverified)","Paid (Verified)",
    "Partially Paid","Overdue","Void/Cancelled","Duplicate"
  ]);
  if (!allowedStatus.has(base.status)) base.status = "Unpaid";

  // Basic date sanity
  const dateOk = (s) => /^(\d{4})-(\d{2})-(\d{2})$/.test(s);
  if (base.issue_date && !dateOk(base.issue_date)) base.issue_date = "";
  if (base.due_date && !dateOk(base.due_date)) base.due_date = "";

  return base;
}

// --- Collect Slack file URLs from either node (PDF or Image lanes) ---
function collectSlackUrls(nodeName) {
  try {
    return $(nodeName).all()
      .map(it =>
        String(
          it.json?.url_private_download ||
          it.json?.file?.url_private_download || // some Slack nodes nest under file
          ""
        )
      )
      .filter(Boolean);
  } catch {
    return [];
  }
}

const urlsDoc = collectSlackUrls('Take Binary Files for Document'); // PDF lane
const urlsImg = collectSlackUrls('Take Binary Files');              // Image lane

// Pick a URL for item index i; prefer same index, then first available
function pickFileUrl(i) {
  return urlsDoc[i] || urlsImg[i] || urlsDoc[0] || urlsImg[0] || "";
}

// --- Process ALL incoming items; each may yield one or many outputs ---
const outputs = [];

for (const [idx, item] of $input.all().entries()) {
  let parsed = safeParseAny(item.json);

  if (parsed && !Array.isArray(parsed) && typeof parsed === "object") {
    const maybeArr = parsed.data || parsed.items || parsed.result || parsed.invoices;
    if (Array.isArray(maybeArr)) parsed = maybeArr;
  }
  if (!Array.isArray(parsed)) parsed = [parsed];

  const fileUrlForThisItem = pickFileUrl(idx);

  for (const obj of parsed) {
    const out = normalizeOne(obj, fileUrlForThisItem);
    outputs.push({ json: out });
  }
}

return outputs;

// Input: many cleaned invoices (each item.json is like your sample)
// Output: clusters of duplicates; each cluster picks one "keep" and lists "drop"

const input = $input.all().map(i => i.json);

// --- Helpers ---
const canonVendor = (v) => {
  if (!v) return "";
  return String(v)
    .toLowerCase()
    .replace(/[.,'’"]/g, " ")
    .replace(/\b(pt|tbk|cv|inc|ltd|llc|llp|plc|corp|co|gmbh|sarl|bv)\b/g, "")
    .replace(/\s+/g, " ")
    .trim();
};
const canonInv = (s) => {
  if (!s) return "";
  return String(s)
    .toUpperCase()
    .replace(/\s+/g, "")
    .replace(/[–—]/g, "-")
    .replace(/[^A-Z0-9\/\-\.]/g, "")
    .replace(/-+/g, "-")
    .trim();
};
const round2 = (n) => Math.round(Number(n || 0) * 100) / 100;

// --- NEW: scoring helpers so PAID wins ---
function parseYmd(s) {
  // "YYYY-MM-DD" -> epoch ms; invalid -> 0
  const m = String(s || "").match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (!m) return 0;
  return Date.UTC(+m[1], +m[2] - 1, +m[3]);
}

function statusRank(status, docPaid) {
  const s = String(status || "").toLowerCase();
  if (docPaid === true || /paid\s*\(verified\)/i.test(status)) return 4;
  if (/paid/i.test(s)) return 3;              // Paid (Unverified)
  if (/partially/i.test(s)) return 2;         // Partially Paid
  if (/overdue/i.test(s)) return 1;           // Overdue still beats Unpaid
  return 0;                                   // Unpaid / others
}

function richness(o) {
  let r = 0;
  if (Array.isArray(o.attachments) && o.attachments.length) r += 2;
  if (o.source_file_id) r += 1;
  if (o.notes) r += 0.5;
  if (Array.isArray(o.line_items)) r += Math.min(2, o.line_items.length * 0.1);
  return r;
}

// choose one representative to keep
function chooseMaster(arr) {
  let best = null, bestScore = -Infinity, bestWhy = "";
  for (const o of arr) {
    const paidRank = statusRank(o.status, o.doc_says_paid); // 0..4
    const newestDate = Math.max(parseYmd(o.due_date), parseYmd(o.issue_date));
    const rich = richness(o);

    // Weights: paidness >>> recency > richness
    const score =
      paidRank * 100 +               // ensure any "Paid" beats "Unpaid"
      (newestDate / 8.64e7) * 0.1 +  // days since epoch * 0.1
      rich;                          // small tie-break

    const why = `paidRank=${paidRank} doc_says_paid=${!!o.doc_says_paid} status=${o.status || ""} newest=${newestDate ? new Date(newestDate).toISOString().slice(0,10) : "-"} richness=${rich}`;

    if (score > bestScore) {
      best = o; bestScore = score; bestWhy = why;
    }
  }
  best._keep_reason_debug = bestWhy;
  best._keep_score_debug = bestScore;
  return best || arr[0];
}

// --- Build clusters ---
const clustersMap = new Map(); // key -> { reason, list: [] }

for (const o of input) {
  const vendor_canon = canonVendor(o.vendor);
  const invoice_no_canon = canonInv(o.invoice_no);
  const currency = (o.currency || "").toUpperCase().trim();
  const amt = round2(o.amount_total);
  const due = (o.due_date || o.issue_date || "").slice(0, 10);

  let key, reason;
  if (invoice_no_canon) {
    key = `INV#${vendor_canon}|${invoice_no_canon}`;
    reason = "SAME_VENDOR + INVOICE_NO";
  } else {
    key = `FALLBACK#${vendor_canon}|${currency}|${amt}|${due}`;
    reason = "NO_INVOICE_NO → SAME_VENDOR + CURRENCY + AMOUNT + DATE";
  }

  if (!clustersMap.has(key)) clustersMap.set(key, { reason, list: [] });
  clustersMap.get(key).list.push({
    ...o,
    _vendor_canon: vendor_canon,
    _invoice_no_canon: invoice_no_canon,
    _fallback_key: !invoice_no_canon
  });
}

// --- Emit only duplicate clusters (>1) ---
const results = [];
for (const [key, { reason, list }] of clustersMap.entries()) {
  if (list.length <= 1) continue;

  const amountSet = Array.from(new Set(list.map(x => round2(x.amount_total))));
  const currencySet = Array.from(new Set(list.map(x => (x.currency || "").toUpperCase().trim())));

  // annotate if amounts differ
  let reasonDetailed = reason;
  if (reason.startsWith("SAME_VENDOR") && amountSet.length > 1) {
    reasonDetailed += " (amount differs across copies)";
  }

  // --- NEW: count paid vs unpaid; prefer paid in keep
  const paidCount = list.filter(x => x.doc_says_paid || /^paid/i.test(String(x.status || ""))).length;
  if (paidCount > 0) reasonDetailed += " | keep prefers PAID copy";

  const keep = chooseMaster(list);
  const drop = list.filter(x => x !== keep);

  results.push({
    json: {
      isDuplicate: true,
      cluster_key: key,
      reason: reasonDetailed,
      count: list.length,
      amounts: amountSet,
      currencies: currencySet,
      paid_count: paidCount,                 // debug/metrics
      keep,                                  // should now be one of C/D (paid)
      drop,
      entries: list
    }
  });
}

// No duplicates? Emit a simple flag.
if (results.length === 0) {
  return [{ json: { isDuplicate: false, info: "No duplicate invoices found." } }];
}

return results;

// n8n Code node — Merge originals with dedup clusters and tag each original
// Upstream nodes:
//   - "Cleans AI Response"  -> contains ORIGINAL parsed items (A, B, C)
//   - Input to this node    -> contains cluster objects from "Internal Check Duplicate Invoice"
// Output: one item per original, with dedup_* fields added

// ---- helpers ----
function canonVendor(v) {
  return String(v || '')
    .toLowerCase()
    .replace(/[^a-z0-9\s]/g, ' ')     // strip punctuation (ASCII-safe)
    .replace(/\b(pt|tbk|inc|ltd|llc)\b/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}
function canonInvoiceNo(inv) {
  return String(inv || '')
    .toUpperCase()
    .replace(/\s+/g, '')
    .replace(/[^A-Z0-9\/\-.]/g, '');
}
function clusterKeyFromItem(it) {
  const vendor = canonVendor(it.vendor);
  const inv = canonInvoiceNo(it.invoice_no);
  if (inv) return `INV#${vendor}|${inv}`;
  const currency = String(it.currency || '').toUpperCase();
  const amt = Number(it.amount_total || it.subtotal || 0) || 0;
  const date = String(it.due_date || it.issue_date || '');
  return `FALLBACK#${vendor}|${currency}|${amt}|${date}`;
}
// Stable-ish signature to match items across steps
function sig(o) {
  return [
    o.source_file_id || '',
    o.source_file_url || '',
    String(o.vendor || '').trim(),
    String(o.invoice_no || '').trim(),
    String(o.issue_date || '').trim(),
    String(o.due_date || '').trim(),
    String(o.currency || '').trim().toUpperCase(),
    String(Number(o.amount_total ?? o.subtotal ?? 0) || 0)
  ].join('||');
}

// ---- 1) load originals (A, B, C) ----
const originals = $('Cleans AI Response').all().map(i => i.json);

// ---- 2) load and normalize clusters from THIS node input ----
const rawIn = $input.all().map(i => i.json);
const clusters = [];
for (const x of rawIn) {
  if (Array.isArray(x)) {
    for (const c of x) if (c && c.cluster_key) clusters.push(c);
  } else if (x && x.cluster_key) {
    clusters.push(x);
  }
}
// If your duplicate checker sometimes emits {clusters:[...]}, support that too
for (const x of rawIn) {
  if (x && Array.isArray(x.clusters)) {
    for (const c of x.clusters) if (c && c.cluster_key) clusters.push(c);
  }
}

// ---- 3) index clusters (by cluster_key) ----
const clusterIndex = new Map();  // cluster_key -> { meta, keepSig, dropSigSet, keepUsed }
for (const c of clusters) {
  const meta = {
    cluster_key: c.cluster_key,
    reason: c.reason || '',
    count: Number(c.count || (c.entries ? c.entries.length : 1)) || 1,
    amounts: c.amounts || [],
    currencies: c.currencies || []
  };
  const keepSig = c.keep ? sig(c.keep) : null;
  const dropSigSet = new Set((c.drop || []).map(sig));
  clusterIndex.set(c.cluster_key, { meta, keepSig, dropSigSet, keepUsed: false });
}

// Try to find matching cluster for an original item
function findClusterForOriginal(item) {
  const s = sig(item);
  for (const [ck, rec] of clusterIndex) {
    if ((rec.keepSig && rec.keepSig === s) || rec.dropSigSet.has(s)) return [ck, rec];
  }
  const ck = clusterKeyFromItem(item);
  return [ck, clusterIndex.get(ck) || null];
}

// ---- 4) merge labels onto originals ----
const out = [];

for (const item of originals) {
  const [ck, rec] = findClusterForOriginal(item);

  if (!rec) {
    out.push({
      json: {
        ...item,
        dedup_cluster_key: ck || '',
        dedup_role: 'unique',
        dedup_count: 1,
        dedup_reason: 'no_cluster_match'
      }
    });
    continue;
  }

  const { meta, keepSig, dropSigSet } = rec;
  const s = sig(item);
  let role;

  if (meta.count <= 1) {
    role = 'unique';
  } else if (keepSig && s === keepSig && !rec.keepUsed) {
    role = 'keep';           // first original that matches the keepSig
    rec.keepUsed = true;     // ensure only one "keep" per cluster
  } else {
    role = 'drop';           // everything else in that cluster is a duplicate
  }

  out.push({
    json: {
      ...item,
      dedup_cluster_key: meta.cluster_key,
      dedup_role: role,                 // "keep" | "drop" | "unique"
      dedup_count: meta.count,
      dedup_reason: meta.reason,
      dedup_amounts: meta.amounts,
      dedup_currencies: meta.currencies
    }
  });
}

return out;

// Code node — Run once for all items
// Preceded by a Merge (Wait for all) so ALL items from both paths arrive here.

const out = [];

function get(v) {
  return typeof v === 'string' ? v.trim() : '';
}

for (const it of $input.all()) {
  const j = it.json || {};

  // Your two possible sources per item
  const fromPdf   = get(j.mergedParsedText);                 // Error3 (PDF path)
  const fromImage = get(j?.ParsedResults?.[0]?.ParsedText);  // Error2 (image path)

  const OCRResult = fromPdf || fromImage || '';
  const OCRSource = fromPdf
    ? 'Error3.mergedParsedText'
    : fromImage
    ? 'Error2.ParsedResults[0].ParsedText'
    : 'none';

  // Keep any file metadata you already have on the item
  out.push({
    json: {
      ...j,
      OCRResult,
      OCRSource
    }
  });
}

return out;

// Run ONCE FOR ALL ITEMS
const out = [];

function windowDates(anchor) {
  if (!anchor) return null;
  const d = new Date(anchor + "T00:00:00Z");
  if (isNaN(d)) return null;
  const s = new Date(d); s.setUTCDate(s.getUTCDate() - 30);
  const e = new Date(d); e.setUTCDate(e.getUTCDate() + 30);
  return [s.toISOString().slice(0,10), e.toISOString().slice(0,10)];
}

for (const { json: j } of $input.all()) {
  const haveInvoiceNo = !!j?.invoice_no;

  // Anchor date:
  // - If we know the invoice number, anchor to invoice-level dates
  // - Otherwise, paid_date is acceptable for finding the invoice by heuristics
  const anchor = haveInvoiceNo
    ? (j?.issue_date || j?.due_date || "")
    : (j?.receipt_hints?.paid_date || j?.issue_date || j?.due_date || "");
  const dates = windowDates(anchor);

  const and = [];

  if (haveInvoiceNo) {
    // Prefer canon properties if your DB has them; else fall back to title field
    if (j.invoice_no_canon) {
      and.push({ property: "Invoice No (Canon)", rich_text: { equals: String(j.invoice_no_canon) } });
      if (j.vendor_canon) {
        and.push({ property: "Vendor (Canon)", rich_text: { equals: String(j.vendor_canon) } });
      }
    } else {
      and.push({ property: "Invoice No", title: { equals: String(j.invoice_no) } });
    }
    if (j.currency) {
      and.push({ property: "Currency", select: { equals: String(j.currency) } });
    }

    // IMPORTANT: no Amount Total filter when Invoice No is present
    // (If you want a belt-and-suspenders check, you can add a WIDE band on amount_total instead,
    // but never use the receipt paid amount.)

  } else {
    // Fallback: vendor + currency + invoice total band + date
    if (j.vendor_canon) {
      and.push({ property: "Vendor (Canon)", rich_text: { equals: String(j.vendor_canon) } });
    } else if (j.vendor) {
      and.push({ property: "Vendor", rich_text: { equals: String(j.vendor) } });
    }
    if (j.currency) {
      and.push({ property: "Currency", select: { equals: String(j.currency) } });
    }
    const total = Number(j?.amount_total);
    if (!Number.isNaN(total) && total > 0) {
      const eps = Math.max(5, total * 0.03); // ± $5 or 3%
      and.push(
        { property: "Amount Total", number: { greater_than_or_equal_to: +(total - eps).toFixed(2) } },
        { property: "Amount Total", number: { less_than_or_equal_to:   +(total + eps).toFixed(2) } },
      );
    }
  }

  if (dates) {
    const [start, end] = dates;
    and.push({
      or: [
        { property: "Issue Date", date: { on_or_after: start, on_or_before: end } },
        { property: "Due Date",   date: { on_or_after: start, on_or_before: end } },
      ]
    });
  }

  const queryBody = { filter: { and }, page_size: haveInvoiceNo ? 10 : 5 };
  out.push({ json: { ...j, queryBody } });
}

return out;

// After "Check DB Invoice" (HTTP). Mode: Run once for all items. Single output.

// Originals aligned by index (from your query-prep node)
const originals = $('Create Query for DB').all().map(i => i.json);

// Toggle if you want partial flows to auto-route (true) or go to manual_review (false)
const ENABLE_PARTIAL = true;

// ---------- Notion helpers ----------
const getTitle  = (p, name) => (p?.properties?.[name]?.title || [])
  .map(t => t.plain_text || '').join('');
const getRich   = (p, name) => (p?.properties?.[name]?.rich_text || [])
  .map(t => t.plain_text || '').join('');
const getSelect = (p, name) => p?.properties?.[name]?.select?.name || '';
const getNumber = (p, name) => (
  typeof p?.properties?.[name]?.number === 'number'
    ? p.properties[name].number
    : null
);
const getDate   = (p, name) => p?.properties?.[name]?.date?.start || '';

const isPaidName = (s) => /^paid/i.test(String(s || ''));

// quick equality for “no-change/dup” check
function coreSame(src, db) {
  const eq = (x,y)=> String(x||'') === String(y||'');
  const numEq=(x,y)=> {
    const nx=Number(x??NaN), ny=Number(y??NaN);
    if (Number.isNaN(nx) && Number.isNaN(ny)) return true;
    return Math.abs(nx - ny) < 0.001;
  };
  return (
    eq(src.vendor, db.vendor) &&
    eq(src.invoice_no, db.invoice_no) &&
    eq(src.currency, db.currency) &&
    numEq(src.amount_total, db.amount_total) &&
    eq(src.issue_date, db.issue_date) &&
    eq(src.due_date, db.due_date)
  );
}

const out = [];
const tolAbs = 0.01;   // absolute amount tolerance
const tolPct = 0.01;   // 1% relative tolerance

$input.all().forEach((it, idx) => {
  const src   = originals[idx] || {};
  const http  = it.json || {};
  const pages = Array.isArray(http.results) ? http.results : [];
  const manyMatches = pages.length > 1;
  const page  = pages[0] || null;

  // ---------- DB snapshot ----------
  const db = page ? {
    page_id:     page.id,
    invoice_no:  getTitle(page, 'Invoice No'),
    vendor:      getRich(page,  'Vendor'),
    currency:    getSelect(page,'Currency'),
    amount_total:getNumber(page,'Amount Total'),
    issue_date:  getDate(page,  'Issue Date'),
    due_date:    getDate(page,  'Due Date'),
    status:      getSelect(page,'Status'),
  } : null;
  const dbPaid = db ? isPaidName(db.status) : false;

  // ---------- mismatches ----------
  const vendorMismatch   = !!(db && db.vendor && src.vendor && db.vendor !== src.vendor);
  const currencyMismatch = !!(db && db.currency && src.currency && db.currency !== src.currency);

  const inAmt = Number(src.amount_total ?? NaN);
  const dbAmt = Number(db?.amount_total ?? NaN);
  const amountMismatch =
    db && Number.isFinite(inAmt) && Number.isFinite(dbAmt) &&
    (Math.abs(inAmt - dbAmt) > tolAbs) &&
    (Math.abs(inAmt - dbAmt) / Math.max(1, dbAmt) > tolPct);

  const fallbackAmbiguous = !src.invoice_no && manyMatches;

  // ---------- derive payment signals from AMOUNTS (ignore labels) ----------
  const statusStr = String(src.status || '');
  const totalIn   = Number(src.amount_total ?? NaN);
  const paidAmtIn = Number(
    (src?.receipt_hints?.paid_amount ?? src.paid_amount) ?? NaN
  );
  const dueIn     = Number(src.amount_due ?? NaN);

  // some payment if: positive paid OR “partial” in status OR doc_says_paid flag
  let hasSomePayment =
    (Number.isFinite(paidAmtIn) && paidAmtIn > tolAbs) ||
    /partial/i.test(statusStr) ||
    src.doc_says_paid === true;

  // full payment primarily by amounts; labels only if amounts missing
  let isFullPayment =
    (Number.isFinite(dueIn)   && dueIn <= tolAbs) ||
    (Number.isFinite(totalIn) && Number.isFinite(paidAmtIn) && (paidAmtIn >= totalIn - tolAbs));

  // if due is positive, it cannot be full—even if the doc says “PAID”
  if (Number.isFinite(dueIn) && dueIn > tolAbs) isFullPayment = false;

  // keep compatibility names used later in logic
  const incomingPaid    = hasSomePayment;
  const partialDetected = hasSomePayment && !isFullPayment;

  // ---------- decide route ----------
  let next_action = 'archive';
  let reason = '';

  if (!page) {
    // No DB page found → choose create_* route
    if (hasSomePayment) {
      if (isFullPayment) {
        next_action = 'create_paid';
        reason = 'no_db_match_paid';
      } else {
        next_action = ENABLE_PARTIAL ? 'create_partial' : 'manual_review';
        reason = ENABLE_PARTIAL ? 'no_db_match_partial_payment' : 'partial_needs_review';
      }
    } else {
      next_action = 'create_unpaid';
      reason = 'no_db_match_unpaid';
    }
  } else {
    // Have a DB page
    if (manyMatches || vendorMismatch || currencyMismatch || fallbackAmbiguous) {
      next_action = 'manual_review';
      reason = manyMatches
        ? 'multiple_db_matches'
        : vendorMismatch
          ? 'vendor_mismatch'
          : currencyMismatch
            ? 'currency_mismatch'
            : 'fallback_ambiguous';
    } else if (hasSomePayment) {
      // There is some payment in the incoming doc
      if (isFullPayment) {
        // mark paid if DB not yet paid; else archive duplicate payment
        if (!dbPaid) {
          next_action = 'update_mark_paid';
          reason = 'mark_paid';
        } else {
          next_action = 'archive';
          reason = 'duplicate_payment_already_paid';
        }
      } else {
        // partial payment path
        if (ENABLE_PARTIAL) {
          next_action = 'update_partial';
          reason = 'partial_payment_detected';
        } else {
          next_action = 'manual_review';
          reason = 'partial_needs_review';
        }
      }
    } else if (amountMismatch) {
      next_action = 'manual_review';
      reason = 'amount_mismatch';
    } else {
      // no payment transition
      const same = coreSame(src, db);
      next_action = 'archive';
      reason = same ? 'duplicate_no_change' : 'no_payment_transition';
    }
  }

  out.push({
    json: {
      ...src,
      db_found:  !!page,
      db_page_id: db?.page_id || '',
      db_status:  db?.status   || '',
      next_action,
      reason,
      _checks: {
        manyMatches,
        vendorMismatch,
        currencyMismatch,
        amountMismatch,
        fallbackAmbiguous,
        hasSomePayment,
        isFullPayment,
        dbPaid
      }
    }
  });
});

return out;

// n8n Code node — prepare line items + compute ONE field for Notion ("Paid Amount")
const out = [];

function two(n){ return Math.round((Number(n)||0)*100)/100; }
function chunk(str, size=1800){ const a=[]; for(let i=0;i<str.length;i+=size)a.push(str.slice(i,i+size)); return a; }
function num(x){ if(x===null||x===undefined||x==="") return NaN; const n=Number(String(x).replace(/[^0-9.-]/g,"")); return isNaN(n)?NaN:n; }
function clamp(n,min,max){ return Math.max(min, Math.min(max, n)); }
function approxEq(a,b,eps=0.01){ return Math.abs(a-b)<=eps; }

for (const item of $input.all()) {
  const j = item.json || {};
  const ccy = (j.currency || '').toUpperCase();
  const items = Array.isArray(j.line_items) ? j.line_items : [];

  // Build line-items text (unchanged)
  const lines = items.map(li => {
    const desc=(li?.description||'').trim();
    const qty = two(li?.qty);
    const up  = two(li?.unit_price);
    const amt = two(li?.amount || qty*up);
    const tail = ccy ? ` ${ccy}` : '';
    return `${qty} × ${desc} @ ${up}${tail} = ${amt}${tail}`;
  });
  const text = lines.join('\n') || '';
  const rich = chunk(text).map(s => ({ type:'text', text:{ content:s }}));

  // Minimal compute for Paid Amount
  const total = num(j.amount_total);
  const due   = num(j.amount_due);
  const delta = num(j?.receipt_hints?.paid_amount ?? j.paid_amount);       // new receipt (e.g., 200)
  const prev  = num(j.db_paid_amount_prev ?? j.invoice?.paid_amount ?? 0); // previous paid if you pass it

  // Prefer set-to from doc (Total - Due); fallback to prev + delta
  let newPaid = Number.isFinite(total) && Number.isFinite(due)
    ? two(clamp(total - due, 0, total))
    : (Number.isFinite(delta) ? two(clamp(prev + delta, 0, Number.isFinite(total)? total : Infinity)) : NaN);

  // If still NaN, don't emit the field to avoid bad writes
  const outJson = {
    ...j,
    line_items_text: text,
    line_items_rich_text: rich,
  };
  if (Number.isFinite(newPaid)) outJson.notion_paid_amount = newPaid;

  // (Optional tiny extra: status, only if you want it — safe to ignore)
  if (Number.isFinite(total) && Number.isFinite(newPaid)) {
    outJson.notion_status =
      approxEq(newPaid, 0)      ? "Unpaid" :
      approxEq(newPaid, total)  ? "Paid (Verified)" :
                                  "Partially Paid";
  }

  out.push({ json: outJson });
}

return out;

