// Build "summary items" text from line_items (one output per input)

function toNum(v) {
  const n = Number(v);
  return Number.isFinite(n) ? n : 0;
}
function clean(s) {
  return String(s ?? "").trim();
}
function fmt(n) {
  // 2dp but strip trailing .00 if integer
  const f = (Math.round(n * 100) / 100).toFixed(2);
  return f.endsWith(".00") ? String(Math.round(n)) : f;
}

const out = [];

for (const { json: rec } of $input.all()) {
  const currency = clean(rec.currency).toUpperCase(); // e.g., "USD"
  const lis = Array.isArray(rec.line_items) ? rec.line_items : [];

  // Group by description + unit_price to collapse duplicates
  const groups = new Map();
  for (const li of lis) {
    const desc = clean(li?.description) || "Item";
    const unit = toNum(li?.unit_price);
    const qty  = toNum(li?.qty);
    const amt  = toNum(li?.amount) || qty * unit;

    const key = `${desc}||${unit}`;
    if (!groups.has(key)) groups.set(key, { desc, unit, qty: 0, amount: 0 });
    const g = groups.get(key);
    g.qty += qty;
    g.amount += amt;
  }

  // Render lines
  const lines = [];
  const rich = [];
  for (const g of groups.values()) {
    const qtyS  = fmt(g.qty);
    const unitS = fmt(g.unit);
    const amtS  = fmt(g.amount);
    const curS  = currency ? ` ${currency}` : "";
    const line  = `${qtyS} × ${g.desc} @ ${unitS}${curS} = ${amtS}${curS}`;
    lines.push(line);
    rich.push({ type: "text", text: { content: line } });
  }

  const summary = lines.join("\n");

  out.push({
    json: {
      ...rec,
      summary_items: summary,              // <-- your "Summary Items" field
      line_items_text: summary,            // keep old name if you need it
      line_items_rich_text: rich           // Notion rich_text-friendly
    }
  });
}

return out;

// Code node: Pick Reviewed Invoice
