const choice = $json?.query?.choice || '';              // approve | unpaid | archive
const invRaw = $json?.query?.inv || '';
const inv = String(invRaw).trim();

const prepared = $('Prepare Line Items before send').all().map(i => i.json);
if (!prepared.length) {
  throw new Error('No items found in "Prepare Line Items before send".');
}

// try exact invoice_no match first
let picked = inv
  ? prepared.find(x => String(x.invoice_no || '').trim() === inv)
  : undefined;

// soft fallback(s)
if (!picked) {
  if (prepared.length === 1) {
    picked = prepared[0];
  } else {
    const canon = s => String(s || '').toLowerCase().replace(/\s+/g, '').trim();
    const invC = canon(inv);
    picked = prepared.find(x => canon(x.invoice_no) === invC) || prepared[0];
  }
}

// annotate with the review decision so the next Switch can route
const out = {
  ...picked,
  review: {
    choice,            // approve | unpaid | archive
    inv,               // invoice_no from the button
    source: 'slack_button'
  }
};

return [{ json: out }];

