// n8n Function Node - Time Slot Matcher
// This code checks if current time matches any of the timer slots

const inputData = $input.all();
const results = [];

// Get current time
const now = new Date();
const currentHour = now.getHours(); // 0-23 format
const currentMinute = now.getMinutes();

console.log(`Current time: ${currentHour}:${currentMinute.toString().padStart(2, '0')}`);

// Function to parse time slot string (e.g., "3-4 AM", "12-2 PM", "2-3 PM")
function parseTimeSlot(timeSlot) {
  if (!timeSlot) return null;

  const slot = timeSlot.trim();
  console.log(`Parsing time slot: "${slot}"`);

  // Match patterns like "3-4 AM", "12-2 PM", "2-3 PM"
  const match = slot.match(/^(\d{1,2})-(\d{1,2})\s*(AM|PM)$/i);

  if (!match) {
    console.log(`Invalid time slot format: ${slot}`);
    return null;
  }

  let startHour = parseInt(match[1]);
  let endHour = parseInt(match[2]);
  const period = match[3].toUpperCase();

  // Convert to 24-hour format
  if (period === 'AM') {
    if (startHour === 12) startHour = 0; // 12 AM = 0 hours (midnight)
    if (endHour === 12) endHour = 0;     // 12 AM = 0 hours (midnight)
  } else if (period === 'PM') {
    if (startHour !== 12) startHour += 12; // Add 12 for PM (except 12 PM stays 12)
    if (endHour !== 12) endHour += 12;     // Add 12 for PM (except 12 PM stays 12)
  }

  // Handle cases where slot spans across midnight (e.g., 11 PM - 1 AM)
  // But for same period slots like "3-4 AM", no adjustment needed

  console.log(`Parsed: ${startHour}:00 - ${endHour}:00`);

  return { startHour, endHour };
}

// Function to check if current time is within a time slot
function isTimeInSlot(timeSlot) {
  const parsed = parseTimeSlot(timeSlot);
  if (!parsed) return false;

  const { startHour, endHour } = parsed;

  // Simple check: current hour should be >= start and < end
  // Example: "3-4 AM" means 03:00 to 03:59 (before 04:00)
  return currentHour >= startHour && currentHour < endHour;
}

// Process each input item
for (const item of inputData) {
  const json = item.json;

  console.log(`Processing record: ${json['Campaign Record Id']}`);

  // Get timer slots from input
  const timer1Slot = json['Timer 1 Slot'];
  const timer2Slot = json['Timer 2 Slot'];
  const timer3Slot = json['Timer 3 Slot'];

  console.log(`Timer slots - 1: "${timer1Slot}", 2: "${timer2Slot}", 3: "${timer3Slot}"`);

  let matching = false;
  let correspondingTimer = null;

  // Check each timer slot
  if (timer1Slot && isTimeInSlot(timer1Slot)) {
    matching = true;
    correspondingTimer = 'Timer 1 Slot';
    console.log(`✅ Match found: Timer 1 Slot (${timer1Slot})`);
  } else if (timer2Slot && isTimeInSlot(timer2Slot)) {
    matching = true;
    correspondingTimer = 'Timer 2 Slot';
    console.log(`✅ Match found: Timer 2 Slot (${timer2Slot})`);
  } else if (timer3Slot && isTimeInSlot(timer3Slot)) {
    matching = true;
    correspondingTimer = 'Timer 3 Slot';
    console.log(`✅ Match found: Timer 3 Slot (${timer3Slot})`);
  }

  if (!matching) {
    console.log(`❌ No matching time slots for current time ${currentHour}:${currentMinute.toString().padStart(2, '0')}`);
  }

  // Create output with all original data plus matching info
  const outputItem = {
    ...json,
    Matching: matching,
    'Corresponding Timer': correspondingTimer,
    'Current Time': `${currentHour}:${currentMinute.toString().padStart(2, '0')}`,
    'Checked At': now.toISOString()
  };

  results.push({ json: outputItem });
}

return results;
