// Retrieve the input data from node "JSON".
let inputData = $node["Generate Query"].json;

// If the output from the "JSON" node is an array, take the first element; otherwise, use it as is.
if (Array.isArray(inputData)) {
  inputData = inputData[0];
}

// Access the "query" property, expecting it to be an array and taking its first element.
const paramsData = inputData.query[0];

// Base URL for Apollo
const baseURL = 'https://app.apollo.io/#/people';

// Array to hold each part of the query string
const queryParts = [];

// Helper function to process and add array parameters to queryParts.
// - Spaces in values will be encoded as %20.
// - Parameter names like 'paramName[]' will have their brackets encoded as 'paramName%5B%5D'.
const addArrayParams = (paramName, values) => {
  values.forEach(val => {
    // Decode any existing '+' in input string to spaces, then URI encode the whole value.
    const decodedValue = String(val).replace(/\+/g, ' ');
    const encodedValue = encodeURIComponent(decodedValue); // Ensures spaces become %20
    queryParts.push(`${paramName}%5B%5D=${encodedValue}`); // Appends paramName%5B%5D=encodedValue
  });
};

// === Construct the query string in the DESIRED ORDER ===

// 1. page=1 (Static)
queryParts.push('page=1');

// 2. Process locations (personLocations[])
// Example: personLocations%5B%5D=London
if (paramsData.location && Array.isArray(paramsData.location) && paramsData.location.length > 0) {
  addArrayParams('personLocations', paramsData.location);
}

// 3. Process organization keyword tags (qOrganizationKeywordTags[])
// Used for searching by company NAME.
// Example: qOrganizationKeywordTags%5B%5D=Paddle
if (paramsData.business && Array.isArray(paramsData.business) && paramsData.business.length > 0) {
  addArrayParams('qOrganizationKeywordTags', paramsData.business);
}

// 4. Static parameters: includedOrganizationKeywordFields[]
// Example: includedOrganizationKeywordFields%5B%5D=tags & includedOrganizationKeywordFields%5B%5D=name
// Using addArrayParams to ensure consistent encoding.
addArrayParams('includedOrganizationKeywordFields', ['tags', 'name']);

// 5. Static parameter: sortByField (value is pre-encoded)
// Example: sortByField=%5Bnone%5D
queryParts.push('sortByField=%5Bnone%5D');

// 6. Static parameter: sortAscending
// Example: sortAscending=false
queryParts.push('sortAscending=false');

// 7. Process qKeywords (person's name/keyword search)
// Example: qKeywords=Rob%20Fletcher (spaces as %20)
if (paramsData.keyword && Array.isArray(paramsData.keyword) && paramsData.keyword.length > 0) {
  // Decode any existing '+' in input string to spaces
  const keywordVal = String(paramsData.keyword[0]).replace(/\+/g, ' ');
  const encodedKeyword = encodeURIComponent(keywordVal); // Ensures spaces become %20
  queryParts.push(`qKeywords=${encodedKeyword}`);
}

// Optional: Process job_title (personTitles[]) if needed in the future.
// Ensure 'personTitles' is the correct Apollo parameter name and uncomment if required.
/*
if (paramsData.job_title && Array.isArray(paramsData.job_title) && paramsData.job_title.length > 0) {
  addArrayParams('personTitles', paramsData.job_title); // This will also use %5B%5D encoding for brackets
}
*/

// Combine all query parts with '&' to form the full query string
const queryString = queryParts.join('&');

// Build the final URL
const finalURL = `${baseURL}?${queryString}`;

// Return the output as an array of objects, with the finalURL nested under a json key.
return [{ json: { finalURL } }];

