// Format structured output into Attio markdown
const data = $json.output;

// Helper function to format attendees
const formatAttendees = (attendees) => {
  return attendees.map(a => {
    let attendeeBlock = `**${a.name}** (${a.email})\n*${a.role}*\n`;
    if (a.lastInteraction) {
      attendeeBlock += `Last interaction: ${a.lastInteraction}\n`;
    }
    if (a.keyPoints && a.keyPoints.length > 0) {
      attendeeBlock += a.keyPoints.map(point => `• ${point}`).join('\n');
    }
    return attendeeBlock;
  }).join('\n\n');
};

// Build the markdown document
const markdown = `# ${data.meetingTitle}

**${data.meetingDateTime.date}**  
**${data.meetingDateTime.time}**

---

## Attendees

${formatAttendees(data.attendees)}

## Context

${data.companyOverview ? `**Company:** ${data.companyOverview}\n\n` : ''}${data.relationshipContext ? `**Relationship:** ${data.relationshipContext}` : ''}

## Meeting Objective

**${data.meetingObjective}**

## Discussion Topics

${(data.discussionTopics || []).map((topic, i) => `${i + 1}. ${topic}`).join('\n')}

## Potential Next Steps

${(data.potentialOutcomes || []).map(outcome => `→ ${outcome}`).join('\n')}`;

return {
  markdown: markdown,
  structured: data
};

