// Format meeting summaries for Slack Block Kit
const data = $json.output || $json;
const meetings = data.meetings || data;

// Helper to truncate text to avoid Slack's block limits
const truncateText = (text, maxLength = 3000) => {
  if (text && text.length > maxLength) {
    return text.substring(0, maxLength - 3) + '...';
  }
  return text || '';
};

// Helper function to format date nicely
const formatDate = () => {
  const date = new Date();
  return date.toLocaleDateString('en-US', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  });
};

// Extract time from the meetingTimePST field (e.g., "Wednesday, August 27 @ 09:00 AM PST")
const extractTime = (timeString) => {
  const match = timeString.match(/@\s*(\d{1,2}:\d{2}\s*[AP]M)/i);
  return match ? match[1] : timeString;
};

// Build the blocks array
let blocks = [
  {
    "type": "header",
    "text": {
      "type": "plain_text",
      "text": "Today's Meeting Brief"
    }
  },
  {
    "type": "context",
    "elements": [
      {
        "text": formatDate(),
        "type": "mrkdwn"
      }
    ]
  },
  {
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `${meetings.length} meetings today. If someone watched you for a week would they think you're serious about achieving your goals?`
    }
  },
  {
    "type": "divider"
  }
];

// Add each meeting as a rich formatted section
meetings.forEach((meeting, index) => {
  const time = extractTime(meeting.meetingTimePST);

  // Meeting header section
  blocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `*${time}*`
    }
  });

  // Build rich text elements for meeting details
  let richTextElements = [];

  // Who section (meeting title)
  richTextElements.push({
    "type": "rich_text_section",
    "elements": [
      {
        "type": "text",
        "text": meeting.meetingTitle,
        "style": {
          "code": true
        }
      },
      {
        "type": "text",
        "text": "\n"
      }
    ]
  });

  // Purpose in quote block
  richTextElements.push({
    "type": "rich_text_quote",
    "elements": [
      {
        "type": "text",
        "text": "Purpose:",
        "style": {
          "bold": true
        }
      },
      {
        "type": "text",
        "text": `\n${truncateText(meeting.purpose, 500)}`
      }
    ]
  });

  // Context section
  richTextElements.push({
    "type": "rich_text_section",
    "elements": [
      {
        "type": "text",
        "text": "\nContext: ",
        "style": {
          "bold": true
        }
      },
      {
        "type": "text",
        "text": truncateText(meeting.keyContext, 500),
        "style": {
          "italic": true
        }
      }
    ]
  });

  // Action items in list
  richTextElements.push({
    "type": "rich_text_list",
    "style": "bullet",
    "elements": [
      {
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": "Action: ",
            "style": {
              "bold": true
            }
          },
          {
            "type": "text",
            "text": truncateText(meeting.action, 400)
          }
        ]
      },
      {
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": "Watch For: ",
            "style": {
              "bold": true
            }
          },
          {
            "type": "text",
            "text": truncateText(meeting.watchFor, 400),
            "style": {
              "italic": true
            }
          }
        ]
      }
    ]
  });

  // Add empty section for spacing
  richTextElements.push({
    "type": "rich_text_section",
    "elements": []
  });

  blocks.push({
    "type": "rich_text",
    "elements": richTextElements
  });

  // Add divider between meetings (except after last)
  if (index < meetings.length - 1) {
    blocks.push({
      "type": "divider"
    });
  }
});

// Add footer
blocks.push({
  "type": "divider"
});

// Create the full Block Kit JSON object
const blockKitJson = {
  "blocks": blocks
};

// Return the properly formatted JSON for n8n Slack node
return {
  // This is the key output - the full JSON object as a string
  blocksUi: JSON.stringify(blockKitJson),

  // Also include for debugging/reference
  rawBlocks: blocks,
  blockKitObject: blockKitJson,

  // Metadata
  meetingCount: meetings.length,
  meetings: meetings,
  hasContent: meetings.length > 0
};
