// Loop over all items
return items.map(item => {
  // Make a copy of the item to avoid mutation
  let newItem = { ...item };

  // Check if the path exists
  if (newItem.json.choices && newItem.json.choices[0].images && newItem.json.choices[0].images[0].image_url) {
    let imageData = newItem.json.choices[0].images[0].image_url.url;

    // Remove the prefix
    if (imageData.startsWith("data:image/png;base64,")) {
      imageData = imageData.replace("data:image/png;base64,", "");
    }

    // Update the value
    newItem.json.choices[0].images[0].image_url.url = imageData;
  }

  return newItem;
});
