// Loop over input items and add a new field called 'myNewField' to the JSON of each one
// Sort the array by sentimentScore in descending order
const sortedItems = $input.all().sort((a, b) => b.json.sentimentScore - a.json.sentimentScore);

// Get the top 4 items
const top4Items = sortedItems.slice(0, 4);

// Return the result in the n8n expected format
return top4Items

// Accessing data from the previous nodes
const openPositions = $('Alpaca_get_open_positions').all();
const topSentiment = $("filter_top_sentiment_score").all();

// Extracting the symbols for easier comparison
const openPositionSymbols = openPositions.map(pos => pos.json.symbol);
const topSentimentSymbols = topSentiment.map(sen => sen.json.stock);

// Table 1: Open positions not in the top sentiment filter
const positionsNotInTopSentiment = openPositions
  .filter(pos => !topSentimentSymbols.includes(pos.json.symbol))
  .map(pos => ({
    symbol: pos.json.symbol,
    market_value: pos.json.market_value,
    qty: pos.json.qty,
    asset_id : pos.json.asset_id
  }));

// Table 2: Symbols from top sentiment not in open positions
const symbolsNotInOpenPositions = topSentiment
  .filter(sen => !openPositionSymbols.includes(sen.json.stock))
  .map(sen => ({
    symbol: sen.json.stock,
  }));

// Calculate the total market value of the first table
const totalMarketValue = positionsNotInTopSentiment.reduce((sum, pos) => {
  return sum + parseFloat(pos.market_value);
}, 0);

// Calculate the value to be assigned to each symbol in the second table
const valuePerSymbol = symbolsNotInOpenPositions.length > 0
  ? totalMarketValue / symbolsNotInOpenPositions.length
  : 0;

// Add the calculated value to each symbol in the second table
const symbolsWithMarketValue = symbolsNotInOpenPositions.map(item => ({
  ...item,
  market_value_per_symbol: valuePerSymbol,
}));

// Returning the two tables as separate outputs
return [
  {
    json: {
      positions_not_in_top_sentiment: positionsNotInTopSentiment,

    symbols_not_in_open_positions: symbolsWithMarketValue,

  }}
];
