// Obtener los datos de entrada
const inputData = $input.first().json;

// Verificar si hay datos válidos
if (!inputData || inputData.code !== 0 || !inputData.data || !inputData.data.tweets) {
  return [];
}

const tweets = inputData.data.tweets;
const results = [];

// Procesar cada tweet
tweets.forEach(tweet => {
  const extractedTweet = {
    text: tweet.text || '',
    retweetCount: tweet.retweetCount || 0,
    replyCount: tweet.replyCount || 0,
    likeCount: tweet.likeCount || 0,
    viewCount: tweet.viewCount || 0,
    // Campos adicionales útiles (opcionales)
    id: tweet.id || '',
    url: tweet.url || '',
    createdAt: tweet.createdAt || '',
    author: tweet.author?.name || ''
  };

  results.push(extractedTweet);
});

return results.map(tweet => ({ json: tweet }));

