// Get raw label from Edit Fields
const rawLabel = $('Loop Over Contacts').first().json.label || '';
console.log("rawLabel:", rawLabel);

// Parse if it‚Äôs a stringified array, otherwise treat as array or single value
let labelNames;
if (typeof rawLabel === 'string') {
  try {
    labelNames = JSON.parse(rawLabel); // Convert "['Gelatin 180-200']" to ["Gelatin 180-200"]
  } catch (e) {
    labelNames = [rawLabel]; // If not valid JSON, treat as single string
  }
} else if (Array.isArray(rawLabel)) {
  labelNames = rawLabel; // Already an array
} else {
  labelNames = rawLabel ? [rawLabel] : []; // Fallback
}

const contactGroups = $input.first().json.contactGroups || [];

console.log("labelNames (parsed):", labelNames);
console.log("contactGroups:", contactGroups);

// Match labels and extract resourceName
const matchedLabels = labelNames.map(label => {
  const group = contactGroups.find(g => 
    g.name.trim().toLowerCase() === label.trim().toLowerCase() && 
    g.groupType === "USER_CONTACT_GROUP"
  );
  console.log(`Searching for: "${label}", Found:`, group);
  return {
    name: label,
    resourceName: group ? [group.resourceName] : null // Wrap resourceName in an array
  };
});

console.log("matchedLabels:", matchedLabels);

return matchedLabels.map(label => ({
  json: {
    name: label.name,
    resourceName: label.resourceName // Now an array like ["contactGroups/40cf29798a92c131"]
  }
}));
