const _ = require("underscore")

let fileInput = null

if (Object.keys($input.first().json).includes("content")) {
  fileInput = JSON.parse(Buffer.from($input.all()[0].json.content, 'base64').toString())
}

const n8nInput = $input.last().json.data

if (!fileInput) {
  return {
    "githubStatus": "new"
  }
}

if (_.isMatch(fileInput, n8nInput)) {
  return {
    "githubStatus": "same"
  }
} else {
  return {
    "githubStatus": "different"
  }
}
