// Get the object from input
const data = $input.first().json.output;

// Flatten all column values into one array
const allValues = Object.values(data).flat();

// Join all values with commas
const result = allValues.join(', ');

// Return the final text as a single field
return [
  {
    json: {
      text: result
    }
  }
];

const columnNames = $input.first().json.output.columnnames;

// Build a single row with column1, column2, etc. as keys and names as values
const row = {};

columnNames.forEach((name, index) => {
  row[`column${index + 1}`] = name;
});

return [
  { json: row }
];
