// Get JSON input
const data = $input.first().json;

// Extract organic results
const results = data.organic_results || [];

const output = results.map(result => {
  return {
    json: {
      title: result.title || "",
      link: result.link || "",
      displayed_link: result.displayed_link || ""
    }
  };
});

return output;

