// In n8n, you can use a Code node to split long messages
const output = $input.first().json.output;
const maxLength = 3600; // Leave some buffer
const chunks = [];

for (let i = 0; i < output.length; i += maxLength) {
    chunks.push(output.substring(i, i + maxLength));
}

return chunks.map(chunk => ({ json: { output: chunk } }));

