return items.map(item => {
  const timestamp = item.json["Time Stamp"]; // <-- corrected key

  // Convert Excel serial timestamp to JS Date
  const excelEpoch = new Date(Date.UTC(1899, 11, 30));
  const jsDate = new Date(excelEpoch.getTime() + timestamp * 24 * 60 * 60 * 1000);

  const day = jsDate.getDay(); // 0=Sunday, 6=Saturday
  const hour = jsDate.getHours();

  // Configure business hours here
  const businessDays = [1, 2, 3, 4, 5]; // Mon-Fri
  const startHour = 9;
  const endHour = 18;

  let isBusinessHours = false;
  if (businessDays.includes(day) && hour >= startHour && hour < endHour) {
    isBusinessHours = true;
  }

  // Format only the time (HH:mm:ss)
  const onlyTime = jsDate.toTimeString().substring(0, 8);

  return {
    json: {
      ...item.json,
      exactDateTime: jsDate.toISOString(), // valid datetime for IF node
      onlyTime,                            // e.g. "19:05:03" (for display only)
      isBusinessHours
    }
  };
});

