const items = $input.all();
const output = [];

// Assume first item is IP Lookup data, second is Webhook data
const ipData = items[0].json; // IP-related data
const webhookData = items[1].json.body; // Webhook payload (name, email, etc.)

// Merge data into a single object
let mergedData = { ...webhookData, ...ipData };

// Calculate leadScore
let score = 0;
// Email valid: +50 (assumed since Verifi Email is bypassed or handled externally)
score += 50;
// Low-risk region: +30 (e.g., US or CA)
if (mergedData.country_code === 'US' || mergedData.region_code === 'CA') score += 30;
// Job role match: +20 (e.g., decision-maker)
if (mergedData.jobRole && ['CEO', 'CTO', 'VP'].includes(mergedData.jobRole.toUpperCase())) score += 20;
mergedData.leadScore = score; // Add score to merged data

// Add timestamp
mergedData.timestamp = new Date().toISOString(); // e.g., 2025-09-22T16:55:00.000Z (10:25 PM IST)

output.push(mergedData);
return output;

const items = $input.all();
const output = [];
items.forEach(item => {
  let score = 0;
  if (item.json.opened) score += 20; // Add more logic for clicks/replies later
  item.json.engagement_score = score;
  output.push(item.json);
});
return output;

const items = $input.all();
const report = {
  total_leads: items.length,
  high_score_leads: items.filter(i => i.json.lead_score >= 70).length,
  avg_engagement: items.reduce((sum, i) => sum + (i.json.engagement_score || 0), 0) / items.length
};
return [{ json: { report } }];
