const items = [];

for (const item of $input.all()) {
  const raw = item.json;

  // Clean up field names by trimming spaces
  const contact = {};
  for (const key in raw) {
    contact[key.trim()] = raw[key];
  }

  items.push({
    json: {
      name: contact.Name || '',
      email: contact.Email || '',
      title: contact.Title || 'Not provided',
      company: contact.Company || 'Not provided',
      phone: contact.Phone || 'Not provided',
      linkedin: contact.LinkedIn || 'Not provided',
      notes: contact.Notes || 'Not provided'
    }
  });
}

return items;

