const items = [];

for (let i = 0; i < $input.all().length; i++) {
  const aiItem = $input.all()[i];
  const originalContact = $('üßπ Clean Contact Data').all()[i].json;

  let analysis;
  try {
    // AI Agent returns response in output field
    const aiResponse = aiItem.json.output || aiItem.json.text || '';

    // Try to extract JSON from the response
    const jsonMatch = aiResponse.match(/\{[^}]+\}/s);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error('No JSON found in response');
    }
  } catch (error) {
    console.log('Failed to parse AI Agent response:', error);
    console.log('AI Response:', aiItem.json);

    // Fallback analysis based on title keywords
    const title = originalContact.title.toLowerCase();
    let fallbackFunction = 'Other';
    let fallbackSeniority = 'Mid Level';

    // Simple keyword matching for fallback
    if (title.includes('sales') || title.includes('account')) fallbackFunction = 'Sales';
    else if (title.includes('marketing')) fallbackFunction = 'Marketing';
    else if (title.includes('engineer') || title.includes('developer')) fallbackFunction = 'Engineering';
    else if (title.includes('hr') || title.includes('people')) fallbackFunction = 'HR';
    else if (title.includes('finance') || title.includes('accounting')) fallbackFunction = 'Finance';

    if (title.includes('ceo') || title.includes('cto') || title.includes('cfo')) fallbackSeniority = 'C-Level';
    else if (title.includes('vp') || title.includes('vice president')) fallbackSeniority = 'VP';
    else if (title.includes('director')) fallbackSeniority = 'Director';
    else if (title.includes('manager')) fallbackSeniority = 'Manager';
    else if (title.includes('senior')) fallbackSeniority = 'Senior Level';
    else if (title.includes('junior') || title.includes('entry')) fallbackSeniority = 'Entry Level';

    analysis = {
      function: fallbackFunction,
      seniority: fallbackSeniority,
      department: 'Unknown',
      confidence_score: 0.3,
      reasoning: 'Fallback analysis due to AI parsing error'
    };
  }

  const enrichedContact = {
    ...originalContact,
    function: analysis.function || 'Other',
    seniority: analysis.seniority || 'Mid Level',
    department: analysis.department || 'Unknown',
    confidence_score: analysis.confidence_score || 0.5,
    reasoning: analysis.reasoning || '',
    analyzed_at: new Date().toISOString()
  };

  items.push({ json: enrichedContact });
}

return items;
