const results = [];

// Process Skyscanner results
if ($node['Merge API Results'].json[0].data) {
  $node['Merge API Results'].json[0].data.forEach(flight => {
    results.push({
      provider: 'Skyscanner',
      price: flight.price,
      currency: flight.currency,
      booking_url: flight.booking_url
    });
  });
}

// Process Travelpayouts results
if ($node['Merge API Results'].json[1].data) {
  $node['Merge API Results'].json[1].data.forEach(flight => {
    results.push({
      provider: 'Travelpayouts',
      price: flight.price,
      currency: flight.currency,
      booking_url: flight.booking_url
    });
  });
}

// Sort by price
results.sort((a, b) => a.price - b.price);

return results;
