// Parse and validate incoming booking data
const inputData = $input.first().json.body ||{};

// Extract booking information
const bookingData = {
  name: inputData.name || '',
  email: inputData.email || '',
  phone: inputData.phone || '',
  date: inputData.date || '',
  time: inputData.time || '',
  source: inputData.source || 'Unknown',
  timestamp: new Date().toISOString()
};

// Validate required fields
const requiredFields = ['name', 'email', 'phone', 'date', 'time'];
const missingFields = requiredFields.filter(field => !bookingData[field]);

if (missingFields.length > 0) {
  return {
    success: false,
    error: `Missing required fields: ${missingFields.join(', ')}`,
    bookingData: null
  };
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(bookingData.email)) {
  return {
    success: false,
    error: 'Invalid email format',
    bookingData: null
  };
}

// Validate phone format (basic validation)
const phoneRegex = /^[+]?[0-9\s\-()]{10,}$/;
if (!phoneRegex.test(bookingData.phone)) {
  return {
    success: false,
    error: 'Invalid phone number format',
    bookingData: null
  };
}

// Validate date format (YYYY-MM-DD)
const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
if (!dateRegex.test(bookingData.date)) {
  return {
    success: false,
    error: 'Invalid date format. Please use YYYY-MM-DD',
    bookingData: null
  };
}

// Validate time format (HH:MM)
const timeRegex = /^\d{2}:\d{2}$/;
if (!timeRegex.test(bookingData.time)) {
  return {
    success: false,
    error: 'Invalid time format. Please use HH:MM',
    bookingData: null
  };
}

return {
  success: true,
  error: null,
  bookingData: bookingData
};

// Business hours validation for Malaysia timezone
const bookingData = $input.first().json.bookingData;

// Parse date and time components
const [year, month, day] = bookingData.date.split('-').map(Number);
const [hour, minute] = bookingData.time.split(':').map(Number);

// Create date object in Malaysia timezone (UTC+8)
// Note: JavaScript Date constructor treats the date as local time when no timezone is specified
// We need to account for Malaysia being UTC+8
const malaysiaTime = new Date(year, month - 1, day, hour, minute, 0);

// Get day of week (0 = Sunday, 1 = Monday, ..., 6 = Saturday)
const dayOfWeek = malaysiaTime.getDay();
const timeInMinutes = hour * 60 + minute;

// Business hours: Mon-Fri (1-5), 9am-9pm (540-1260 minutes)
// Excluding: 12-2pm (720-840 minutes), 6-8pm (1080-1200 minutes)
const isWeekday = dayOfWeek >= 1 && dayOfWeek <= 5;
const isWithinBusinessHours = timeInMinutes >= 540 && timeInMinutes <= 1260;
const isNotLunchBreak = !(timeInMinutes >= 720 && timeInMinutes <= 840);
const isNotDinnerBreak = !(timeInMinutes >= 1080 && timeInMinutes <= 1200);

// Check if time is valid
const isValidTime = isWeekday && isWithinBusinessHours && isNotLunchBreak && isNotDinnerBreak;

// Format the requested datetime for Google Calendar (in Malaysia timezone)
// Create proper ISO string with Malaysia timezone offset
const startDateTime = new Date(year, month - 1, day, hour, minute, 0).toISOString().replace('Z', '+08:00');
const endDateTime = new Date(year, month - 1, day, hour + 1, minute, 0).toISOString().replace('Z', '+08:00');

// Create proper ISO string with Malaysia timezone offset
const startDateTimeCal = new Date(year, month - 1, day, hour, minute, 0).toISOString().replace('Z', '+08:00').replace('.000', '');
const endDateTimeCal = new Date(year, month - 1, day, hour + 1, minute, 0).toISOString().replace('Z', '+08:00').replace('.000', '');

let errorMessage = '';
if (!isWeekday) {
  errorMessage = 'Sorry, we only accept bookings on weekdays (Monday to Friday).';
} else if (!isWithinBusinessHours) {
  errorMessage = 'Sorry, we only accept bookings between 9:00 AM and 9:00 PM Malaysia time.';
} else if (!isNotLunchBreak) {
  errorMessage = 'Sorry, we are closed for lunch from 12:00 PM to 2:00 PM Malaysia time.';
} else if (!isNotDinnerBreak) {
  errorMessage = 'Sorry, we are closed for dinner from 6:00 PM to 8:00 PM Malaysia time.';
}


return {
  isValidTime: isValidTime,
  errorMessage: errorMessage,
  bookingData: bookingData,
  startDateTimeCal: startDateTimeCal,
  endDateTimeCal: endDateTimeCal,
  startDateTime: startDateTime,
  endDateTime: endDateTime,


  malaysiaTime: malaysiaTime.toLocaleString('en-MY', { timeZone: 'Asia/Kuala_Lumpur' }),
  dayOfWeek: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][dayOfWeek],
  debug: {
    parsedDate: bookingData.date,
    parsedTime: bookingData.time,
    dayOfWeek: dayOfWeek,
    timeInMinutes: timeInMinutes,
    isWeekday: isWeekday,
    isWithinBusinessHours: isWithinBusinessHours,
    isNotLunchBreak: isNotLunchBreak,
    isNotDinnerBreak: isNotDinnerBreak
  }
};

function checkCalendar(calendarEvents, bookingData,startDateTime,endDateTime){



// Filter out events that might conflict
const conflictingEvents = calendarEvents.filter(event => {
  const eventStart = new Date(event.json.start?.dateTime || event.json.start?.date);
  const eventEnd = new Date(event.json.end?.dateTime || event.json.end?.date);

  // Check for overlap
  return (eventStart < new Date(endDateTime) && eventEnd > new Date(startDateTime));
});

const isAvailable = conflictingEvents.length === 0;

let availabilityMessage = '';
if (!isAvailable) {
  const conflictingTimes = conflictingEvents.map(event => {
    const start = new Date(event.json.start?.dateTime || event.json.start?.date);
    return start.toLocaleString('en-MY', { timeZone: 'Asia/Kuala_Lumpur' });
  }).join(', ');

  availabilityMessage = `Sorry, the requested time slot is not available. We have existing bookings at: ${conflictingTimes}. Please choose a different time.`;
}

return {
  isAvailable: isAvailable,
  availabilityMessage: availabilityMessage,
  conflictingEvents: conflictingEvents,
  bookingData: bookingData,
  startDateTime: startDateTime,
  endDateTime: endDateTime
};


} 


