// Parse and validate incoming date data
const inputData = $('Booking Timeslot webhook').first().json.body;

// Extract date information
const checkData = {
  date: inputData.date || '',
  timestamp: new Date().toISOString()
};

// Validate required fields
if (!checkData.date) {
  return {
    success: false,
    error: 'Missing required field: date',
    checkData: null
  };
}

// Validate date format (YYYY-MM-DD)
const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
if (!dateRegex.test(checkData.date)) {
  return {
    success: false,
    error: 'Invalid date format. Please use YYYY-MM-DD',
    checkData: null
  };
}

// Parse date components
const [year, month, day] = checkData.date.split('-').map(Number);
const checkDate = new Date(year, month - 1, day);
const dayOfWeek = checkDate.getDay();

// Check if it's a weekend
const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

return {
  success: true,
  error: null,
  checkData: checkData,
  parsedDate: {
    year: year,
    month: month,
    day: day,
    dayOfWeek: dayOfWeek,
    dayName: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][dayOfWeek],
    isWeekend: isWeekend
  }
};

// Check if the date is a public holiday and calendar conflicts
const holidayEvents = $('Check Public Holiday Calendar').all();
const calendarEvents = $('Check Calendar Availability').all();
const dateInfo = $('Validate Date1').first().json;
const configTimeSlots = $('ConfigTimeSlots').first().json.configuredAvailableTimeSlots

// Check if there are any holiday events for this date
const isHoliday = holidayEvents && holidayEvents[0] && holidayEvents[0].json && holidayEvents[0].json.summary;
let holidayName = '';
let holidayMessage = '';

if (isHoliday) {
  // Get the first holiday event name
  holidayName = holidayEvents[0].json.summary || 'Public Holiday';
  holidayMessage = `This is a non-working day: ${holidayName}`;
}

// Check if it's a weekend
const isWeekend = dateInfo.parsedDate.isWeekend;
let weekendMessage = '';

if (isWeekend) {
  weekendMessage = `This is a weekend (${dateInfo.parsedDate.dayName})`;
}

// Determine if it's a working day
const isWorkingDay = !isHoliday && !isWeekend;

// Generate available time slots for working days
let availableSlots = [];
if (isWorkingDay) {
  // Business hours: 9am-9pm, excluding 12-2pm and 6-8pm
  const slots =  configTimeSlots ||[];

  // Check each slot against calendar events for conflicts
  const selectedDate = dateInfo.checkData.date;

  slots.forEach(slot => {
    // Create start and end times for this slot
    const slotStartTime = `${selectedDate}T${slot.time}:00+08:00`;
    const slotEndTime = `${selectedDate}T${String(parseInt(slot.time.split(':')[0]) + 1).padStart(2, '0')}:${slot.time.split(':')[1]}:00+08:00`;

    // Check if any calendar events conflict with this slot
    const hasConflict = calendarEvents.some(event => {
      const eventStart = new Date(event.json.start?.dateTime || event.json.start?.date);
      const eventEnd = new Date(event.json.end?.dateTime || event.json.end?.date);
      const slotStart = new Date(slotStartTime);
      const slotEnd = new Date(slotEndTime);

      // Check for overlap
      return (eventStart < slotEnd && eventEnd > slotStart);
    });

    if (hasConflict) {
      slot.available = false;
      slot.status = 'booked';
    }
  });

  availableSlots = slots;
}

return {
  success: true,
  isWorkingDay: isWorkingDay,
  isHoliday: isHoliday,
  isWeekend: isWeekend,
  holidayName: holidayName,
  holidayMessage: holidayMessage,
  weekendMessage: weekendMessage,
  availableSlots: availableSlots,
  dateInfo: dateInfo,
  holidayEvents: holidayEvents,
  calendarEvents: calendarEvents,
  totalConflicts: calendarEvents.length
};
