/**
 * Nettoie un num√©ro de t√©l√©phone en supprimant tous les caract√®res non num√©riques
 * @param {string} phoneNumber - Num√©ro de t√©l√©phone brut
 * @returns {string} - Num√©ro nettoy√©
 */
function cleanPhoneNumber(phoneNumber) {
  if (!phoneNumber || typeof phoneNumber !== 'string') {
    return '';
  }

  // Supprimer tous les caract√®res non num√©riques sauf le + au d√©but
  let cleaned = phoneNumber.trim();

  // Si le num√©ro commence d√©j√† par +, on le garde tel quel
  if (cleaned.startsWith('+')) {
    return cleaned.replace(/[^\d+]/g, '');
  }

  // Sinon, supprimer tous les caract√®res non num√©riques
  return cleaned.replace(/[^\d]/g, '');
}

/**
 * D√©tecte le pays et formate le num√©ro de t√©l√©phone
 * @param {string} phoneNumber - Num√©ro de t√©l√©phone nettoy√©
 * @returns {Object} - Objet avec le num√©ro format√© et les informations du pays
 */
function detectAndFormatPhoneNumber(phoneNumber) {
  // Si le num√©ro commence d√©j√† par +, on consid√®re qu'il est d√©j√† format√©
  if (phoneNumber.startsWith('+')) {
    return {
      formatted: phoneNumber,
      detected_country: 'Already formatted',
      confidence: 'high'
    };
  }

  // Parcourir chaque pays pour trouver une correspondance
  for (const [countryCode, config] of Object.entries(countryPrefixes)) {
    for (const pattern of config.patterns) {
      if (pattern.test(phoneNumber)) {
        return {
          formatted: config.format(phoneNumber),
          detected_country: countryCode,
          confidence: 'high',
          original: phoneNumber
        };
      }
    }
  }

  // Si aucun pattern ne correspond, essayer de deviner par la longueur
  const length = phoneNumber.length;

  // Longueurs typiques par d√©faut (France comme fallback pour l'Europe)
  if (length === 10 && phoneNumber.startsWith('0')) {
    return {
      formatted: countryPrefixes.FR.format(phoneNumber),
      detected_country: 'FR',
      confidence: 'medium',
      original: phoneNumber,
      reason: 'Fallback: 10 digits starting with 0 (assumed French)'
    };
  }

  if (length === 9 && !phoneNumber.startsWith('0')) {
    return {
      formatted: `+33${phoneNumber}`,
      detected_country: 'FR',
      confidence: 'low',
      original: phoneNumber,
      reason: 'Fallback: 9 digits without 0 (assumed French mobile)'
    };
  }

  // Si on ne peut pas d√©tecter, retourner le num√©ro original avec un avertissement
  return {
    formatted: phoneNumber,
    detected_country: 'Unknown',
    confidence: 'none',
    original: phoneNumber,
    warning: 'Could not detect country, returned original number'
  };
}

