      });
      continue;
    }

    // Nettoyer et formater le num√©ro
    const cleanedNumber = cleanPhoneNumber(rawPhoneNumber);

    if (!cleanedNumber) {
      processedItems.push({
        ...item,
        json: {
          ...item.json,
          phone_formatted: '+330000000000'
        }
      });
      continue;
    }

    // D√©tecter et formater
    const result = detectAndFormatPhoneNumber(cleanedNumber);

    // Ajouter seulement le num√©ro format√© √† l'item
    processedItems.push({
      ...item,
      json: {
        ...item.json,
        // Num√©ro format√© avec indicatif
        phone_formatted: result.formatted
