  data: {
    target: ResourceLink;
  };
  content: Text[];
}

export interface TableCell extends Block {
  nodeType: BLOCKS.TABLE_HEADER_CELL | BLOCKS.TABLE_CELL;
  data: {
    colspan?: number;
