// Split the markdown into sections using ## as the logical chunk point
const splitByHeading = (markdown, headingLevel = 3) => {
  const headingRegex = new RegExp(`(?=^${'#'.repeat(headingLevel)}\\s)`, 'gm');
  return markdown.split(headingRegex).filter(chunk => chunk.trim());
};

// Get the first input item's content
const input = items[0].json;
const chunks = splitByHeading(input.content);

return chunks.map((chunk, index) => {
  return {
    json: {
      index,
      slug: input.slug?.trim(),
      title: input.title?.trim(),
      contentChunk: chunk.trim(),
    }
  };
});

const combinedContent = [];

for (const item of items) {
  let raw = item.json;

  // Handle if AI agent returned a stringified JSON inside a property like `output`
  if (typeof raw === 'object' && typeof raw.output === 'string') {
    try {
      raw = JSON.parse(raw.output);
    } catch (e) {
      throw new Error(`Failed to parse JSON from agent output:\n${raw.output}`);
    }
  }

  // Handle if AI agent returned plain stringified JSON (not wrapped in a key)
  if (typeof raw === 'string') {
    try {
      raw = JSON.parse(raw);
    } catch (e) {
      throw new Error(`Failed to parse raw JSON string:\n${raw}`);
    }
  }

  // Add content from parsed result
  if (raw?.content?.length) {
    combinedContent.push(...raw.content);
  }
}

return [
  {
    json: {
      nodeType: 'document',
      data: {},
      content: combinedContent
    }
  }
];
