/**
 * This Function node takes the script from the previous node
 * and splits it using "voice1:" and "voice2:" as delimiters.
 * Each resulting segment retains the respective identifier.
 */

const script = $input.first().json.message.content || "";

// Ensure consistent line breaks
const normalizedScript = script.replace(/\r\n/g, "\n");

// Split the script while keeping "voice1:" and "voice2:" in the result
const segments = normalizedScript.split(/(?=(?:voice1:|voice2:))/g).map(s => s.trim()).filter(Boolean);

// Return one item per segment
return segments.map(segment => {
  return {
    json: {
      segment
    }
  };
});

