/**
 * This Code node will:
 * 1. Gather all file paths from the incoming items (assuming each item has `item.json.filePath`).
 * 2. Build a single text string, each line in FFmpeg concat format: `file '/path/to/audio.mp3'`
 * 3. Convert that text to binary (Base64) so the next node ("Write Binary File") can save it as `concat_list.txt`.
 */

const items = $input.all();

// Build the concat list
let concatListText = '';

items.forEach((item, index) => {
  let filePath;


  // Use only fileName for the rest
    filePath = item.json.fileName;


  if (filePath) {
    concatListText += `file '${filePath}'\n`;
  }
});

// Convert the text to a Buffer, then to Base64
const buffer = Buffer.from(concatListText, 'utf-8');
const base64Data = buffer.toString('base64');

// Return a single item containing the binary data
return [
  {
    json: {},
    binary: {
      data: {
        data: base64Data,
        mimeType: 'text/plain',
        fileName: 'concat_list.txt'
      }
    }
  }
];
