// Fetch authorized users configuration
const authorizedUsers = {
  telegram_ids: $json.telegram_ids,
  usernames: $json.usernames
};

const userId = $('Switch').item.json.message.from.id.toString();
const username = $('Switch').item.json.message.from.username;

// Check if user is authorized (by id or username)
const isAuthorized =
  authorizedUsers.telegram_ids.includes(userId) ||
  authorizedUsers.usernames.includes(username);

// Return user info and original message
return {
  json: {
    user_id: userId,
    username: username,
    message_text: $('Switch').item.json.message.text,
    chat_id: $('Switch').item.json.message.chat.id,
    message_id: $('Switch').item.json.message.message_id,
    timestamp: new Date().toISOString(),
    authorized: isAuthorized
  }
};

// Generate unique post ID
const now = new Date();
const dateStr = now.toISOString().replace(/[-:T]/g, '').slice(0, 14);
const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
const postId = `${dateStr}_${random}`;

return {
  json: {
    ...$input.item.json,
    post_id: postId,
    created_at: now.toISOString()
  }
};

// Enhanced Parse Classification for better integration
const inputJson = $input.item.json;

// Extract classification results
const classificationResult = {
  input_type: $json.output.input_type || 'Topic',
  urls: $json.output.urls || [],
  topics: $json.output.topics || [],
  content: $json.output.content || '',
  processing_instructions: $json.output.processing_instructions || ''
};

// Preserve original request data for tracking
const originalRequestData = {
  post_id: $('Log Initial Request').item.json.post_id,
  chat_id:$('Generate Post ID').item.json.chat_id,
  message_id:$('Generate Post ID').item.json.message_id,
  timestamp: inputJson.timestamp || new Date().toISOString(),
  user_id: $('Log Initial Request').item.json.user_id || '',
  username: $('Log Initial Request').item.json.user_name || '',
  original_message: $('Log Initial Request').item.json.original_message || inputJson.message_text || ''
};

// Add processing metadata
const processingMetadata = {
  classification_timestamp: new Date().toISOString(),
  has_urls: classificationResult.urls.length > 0,
  has_topics: classificationResult.topics.length > 0,
  has_content: classificationResult.content.length > 0,
  url_count: classificationResult.urls.length,
  topic_count: classificationResult.topics.length,
  content_length: classificationResult.content.length,
  requires_scraping: classificationResult.urls.length > 0,
  requires_search: classificationResult.topics.length > 0,
  processing_complexity: determineComplexity(classificationResult)
};

// Determine processing complexity
function determineComplexity(result) {
  const factors = [
    result.urls.length > 0,
    result.topics.length > 0,
    result.content.length > 0
  ].filter(Boolean).length;

  if (factors === 0) return 'simple';
  if (factors === 1) return 'moderate';
  if (factors >= 2) return 'complex';
}

// Return comprehensive data for downstream processing
return {
  json: {
    ...originalRequestData,
    ...classificationResult,
    ...processingMetadata,

    // Add routing hints for Content Router
    route_to_scraper: classificationResult.urls.length > 0,
    route_to_search: classificationResult.topics.length > 0,
    route_to_direct: classificationResult.urls.length === 0 && 
                     classificationResult.topics.length === 0,

    // Prepare data for Google Sheets tracking
    extracted_urls: classificationResult.urls.join(', '),
    identified_topics: classificationResult.topics.join(', '),
    input_classification: classificationResult.input_type,
    processing_stage: 'classified'
  }
};

const urls = $input.item.json.output?.urls || $input.item.json.urls || [];

if (urls.length === 0) {
  // No URLs to process, pass through original data
  return {
    json: {
      ...$input.item.json,
      urls_to_process: [],
      total_urls: 0,
      processing_stage: 'no_urls'
    }
  };
}

// Create individual items for each URL to be processed
const urlItems = urls.map((url, index) => ({
  json: {
    ...$input.item.json,
    ...$input.item.json.output,
    current_url: url,
    url_index: index,
    total_urls: urls.length,
    processing_stage: 'url_extraction'
  }
}));
// Return array of items for processing
return urlItems;

// Get all inputs from the loop
const allInputs = $input.all();
console.log("Processing all loop outputs:", allInputs.length, "items");

let mergedContent = '';
let allSourceUrls = [];
let allMetadataUrls = [];
let overallSuccess = true;

// Process each input from the loop
for (const inputItem of allInputs) {
  const scrapedData = inputItem.json;
  let processedContent = '';
  let scrapingSuccess = false;
  let metadataUrl = '';

  try {
    // Handle Firecrawl response structure
    if (scrapedData.data && scrapedData.data.markdown) {
      processedContent = scrapedData.data.markdown;

      // Extract URL from metadata if available
      if (scrapedData.data.metadata && scrapedData.data.metadata.ogUrl) {
        metadataUrl = scrapedData.data.metadata.ogUrl;
        allMetadataUrls.push(metadataUrl);
      }

      scrapingSuccess = true;
    }
    // Handle alternative response structure
    else if (scrapedData.markdown) {
      processedContent = scrapedData.markdown;

      // Extract URL from metadata if available
      if (scrapedData.metadata && scrapedData.metadata.ogUrl) {
        metadataUrl = scrapedData.metadata.ogUrl;
        allMetadataUrls.push(metadataUrl);
      }

      scrapingSuccess = true;
    }
    // Handle plain text content
    else if (scrapedData.content) {
      processedContent = scrapedData.content;

      // Extract URL from metadata if available
      if (scrapedData.metadata && scrapedData.metadata.ogUrl) {
        metadataUrl = scrapedData.metadata.ogUrl;
        allMetadataUrls.push(metadataUrl);
      }

      scrapingSuccess = true;
    }
    else {
      throw new Error('No content found in Firecrawl response');
    }
  } catch (error) {
    processedContent = `Error scraping ${scrapedData.current_url}: ${error.message}`;
    scrapingSuccess = false;
  }

  // Use metadata URL if available, otherwise fall back to current_url
  const sourceUrl = metadataUrl || scrapedData.current_url;

  // Add to merged content with source separator
  if (sourceUrl) {
    mergedContent += `\n\n--- Source: ${sourceUrl} ---\n\n`;
    allSourceUrls.push(sourceUrl);
  }
  mergedContent += processedContent;

  // Update overall success (false if any scraping failed)
  if (!scrapingSuccess) {
    overallSuccess = false;
  }
}

// Remove duplicate URLs and create final source URL list
const uniqueSourceUrls = [...new Set(allSourceUrls)];
const uniqueMetadataUrls = [...new Set(allMetadataUrls)];

console.log("Extracted metadata URLs:", uniqueMetadataUrls);
console.log("All source URLs:", uniqueSourceUrls);

// Return only the required fields
return {
  json: {
    merged_content: mergedContent.trim(),
    scraping_success: overallSuccess,
    source_url: uniqueSourceUrls.join(', '),
    metadata_urls: uniqueMetadataUrls.join(', '), // Additional field with metadata URLs
    processing_stage: 'content_merged'
  }
};

// Optimized for direct JSON access without looping
const allInputs = $input.all();

console.log("Total inputs received:", allInputs.length);

// Initialize the complete output structure
let processedData = {
  // Original input data
  post_id: '',
  user_id: '',
  username: '',
  chat_id: '',
  message_id:'',
  message_text: '',
  input_type: '',
  urls: [],
  topics: [],
  content: '',
  processing_instructions: '',

  // Scraped content from URLs
  scraped_content: '',
  scraped_urls: [],
  scraped_summary: '',

  // Brave search results
  brave_search_results: '',
  brave_search_performed: false,
  brave_search_query: '',

  // Processing metadata
  content_sources: [],
  total_scraped_urls: 0,
  successful_scrapes: 0,
  failed_scrapes: 0,
  processing_timestamp: new Date().toISOString(),
  routes_taken: [],

  // Final merged content for LLM
  merged_content_for_llm: ''
};

// Direct access to specific inputs based on expected structure
console.log("Processing inputs with direct JSON access...");
console.log("Input structures:", allInputs.map((input, index) => ({
  index,
  keys: Object.keys(input.json || {}).slice(0, 10),
  hasArray: Array.isArray(input.json),
  jsonType: typeof input.json
})));

// Look for Parse Classification data in any input
const parseInput = allInputs.find(input => {
  if (!input.json) return false;

  // Handle array structure
  if (Array.isArray(input.json)) {
    return input.json.some(item => 
      item.post_id !== undefined || 
      item.input_type !== undefined || 
      item.processing_stage === 'classified'
    );
  }

  // Handle direct object
  return input.json.post_id !== undefined || 
         input.json.input_type !== undefined || 
         input.json.processing_stage === 'classified';
});

if (parseInput) {
  console.log("Found Parse Classification data");

  let parseData;
  if (Array.isArray(parseInput.json)) {
    // Find the classification data in the array
    parseData = parseInput.json.find(item => 
      item.post_id !== undefined || 
      item.input_type !== undefined || 
      item.processing_stage === 'classified'
    );
  } else {
    parseData = parseInput.json;
  }

  if (parseData) {
    console.log("Processing Parse Classification data:", Object.keys(parseData));

    processedData.post_id = parseData.post_id || '';
    processedData.chat_id = parseData.chat_id || '';
    processedData.message_id = parseData.message_id || '';
    processedData.user_id = parseData.user_id || '';
    processedData.username = parseData.username || '';
    processedData.chat_id = parseData.chat_id || '';
    processedData.message_text = parseData.original_message || parseData.message_text || '';
    processedData.input_type = parseData.input_type || '';

    // Handle URLs - could be array or string
    if (parseData.urls && Array.isArray(parseData.urls)) {
      processedData.urls = parseData.urls;
    } else if (parseData.extracted_urls) {
      processedData.urls = Array.isArray(parseData.extracted_urls) ? 
        parseData.extracted_urls : [parseData.extracted_urls];
    } else {
      processedData.urls = [];
    }

    // Handle topics - could be array or string  
    if (parseData.topics && Array.isArray(parseData.topics)) {
      processedData.topics = parseData.topics;
    } else if (parseData.identified_topics) {
      processedData.topics = Array.isArray(parseData.identified_topics) ? 
        parseData.identified_topics : [parseData.identified_topics];
    } else {
      processedData.topics = [];
    }

    processedData.content = parseData.content || '';
    processedData.processing_instructions = parseData.processing_instructions || '';
    processedData.routes_taken.push('Parse Classification');
  }
}

// Input 2: Scraped Content (look for merged_content or scraped_content)
const scrapedInput = allInputs.find(input => {
  if (!input.json) return false;

  // Handle array structure
  if (Array.isArray(input.json)) {
    return input.json.some(item => 
      item.merged_content || 
      item.scraped_content || 
      item.processing_stage === 'content_merged'
    );
  }

  // Handle direct object
  return input.json.merged_content || 
         input.json.scraped_content || 
         input.json.processing_stage === 'content_merged';
});

if (scrapedInput) {
  console.log("Found Scraped Content");

  let scrapedData;
  if (Array.isArray(scrapedInput.json)) {
    // Find the scraped data in the array
    scrapedData = scrapedInput.json.find(item => 
      item.merged_content || item.scraped_content || item.processing_stage === 'content_merged'
    ) || scrapedInput.json[0]; // fallback to first item if structure is different
  } else {
    scrapedData = scrapedInput.json;
  }

  if (scrapedData) {
    console.log("Processing Scraped Content:", Object.keys(scrapedData).slice(0, 10));

    // Use either scraped_content or merged_content
    processedData.scraped_content = scrapedData.scraped_content || scrapedData.merged_content || '';

    // Handle URLs from various sources
    if (scrapedData.scraped_urls && Array.isArray(scrapedData.scraped_urls)) {
      processedData.scraped_urls = scrapedData.scraped_urls;
    } else if (scrapedData.source_url) {
      processedData.scraped_urls = scrapedData.source_url.split(', ').filter(url => url.trim());
    } else if (scrapedData.metadata_urls) {
      processedData.scraped_urls = Array.isArray(scrapedData.metadata_urls) ? 
        scrapedData.metadata_urls : [scrapedData.metadata_urls];
    } else {
      processedData.scraped_urls = [];
    }

    processedData.successful_scrapes = scrapedData.scraping_success !== false ? processedData.scraped_urls.length : 0;
    processedData.total_scraped_urls = processedData.scraped_urls.length;
    processedData.scraped_summary = `Successfully scraped ${processedData.successful_scrapes} URLs`;

    if (scrapedData.scraping_success !== false && processedData.scraped_urls.length > 0) {
      processedData.content_sources.push(`Web Scraping (${processedData.scraped_urls.length} URLs)`);
    } else {
      processedData.content_sources.push('Web Scraping (Failed)');
    }
    processedData.routes_taken.push('URL Processing');
  }
}

// Input 3: Brave Search Results (look for web.results)
const searchInput = allInputs.find(input => {
  if (!input.json) return false;

  // Handle array structure
  if (Array.isArray(input.json)) {
    return input.json.some(item => item.web && item.web.results);
  }

  // Handle direct object
  return input.json.web && input.json.web.results;
});

if (searchInput) {
  console.log("Found Brave Search Results");

  let searchData;
  if (Array.isArray(searchInput.json)) {
    searchData = searchInput.json.find(item => item.web && item.web.results);
  } else {
    searchData = searchInput.json;
  }

  if (searchData) {
    console.log("Processing Brave Search Results");

    processedData.brave_search_performed = true;
    processedData.brave_search_query = searchData.query || processedData.topics.join(' ');

    if (searchData.web.results && searchData.web.results.length > 0) {
      const formattedResults = searchData.web.results.slice(0, 5).map((result, index) => {
        return [
          `**Result ${index + 1}:**`,
          `Title: ${result.title}`,
          `URL: ${result.url}`,
          `Description: ${result.description || 'No description available'}`,
          result.age ? `Published: ${result.age}` : ''
        ].filter(line => line).join('\n');
      }).join('\n\n');

      processedData.brave_search_results = formattedResults;
      processedData.content_sources.push(`Brave Search (${searchData.web.results.length} results)`);
    }
    processedData.routes_taken.push('Topic Search');
  }
}

// Input 4: Content Router False route (look for bypass_reason or similar markers)
const routerInput = allInputs.find(input => {
  if (!input.json) return false;

  // Handle array structure
  if (Array.isArray(input.json)) {
    return input.json.some(item => 
      item.bypass_reason || 
      item.route_taken === 'false' || 
      item.processing_stage === 'bypassed'
    );
  }

  // Handle direct object
  return input.json.bypass_reason || 
         input.json.route_taken === 'false' ||
         input.json.processing_stage === 'bypassed';
});

if (routerInput) {
  console.log("Found Content Router False route");

  let routerData;
  if (Array.isArray(routerInput.json)) {
    routerData = routerInput.json.find(item => 
      item.bypass_reason || item.route_taken === 'false' || item.processing_stage === 'bypassed'
    );
  } else {
    routerData = routerInput.json;
  }

  if (routerData) {
    processedData.routes_taken.push('Content Router (False)');
    if (routerData.bypass_reason) {
      processedData.bypass_reason = routerData.bypass_reason;
    }
  }
}

// Build the final merged content for LLM
let llmContent = [];
let sectionCount = 0;

// Add processing instructions if available
if (processedData.processing_instructions) {
  llmContent.push(`## PROCESSING INSTRUCTIONS\n${processedData.processing_instructions}`);
  sectionCount++;
}

// Add original content if provided
if (processedData.content) {
  llmContent.push(`## ORIGINAL CONTENT TO ENHANCE\n${processedData.content}`);
  sectionCount++;
}

// Add topics if provided
if (processedData.topics && processedData.topics.length > 0) {
  const topicsText = Array.isArray(processedData.topics) ? 
    processedData.topics.filter(topic => topic).join('\n- ') : 
    processedData.topics;
  llmContent.push(`## TOPICS TO COVER\n- ${topicsText}`);
  sectionCount++;
}

// Add scraped content with source attribution
if (processedData.scraped_content) {
  llmContent.push(`## WEB CONTENT (Scraped from URLs)\n${processedData.scraped_content}`);
  sectionCount++;
}

// Add Brave search results
if (processedData.brave_search_results) {
  llmContent.push(`## SEARCH RESULTS (Latest Information)\n${processedData.brave_search_results}`);
  sectionCount++;
}

// Create context summary for LLM
const contextSummary = [
  `Input Type: ${processedData.input_type || 'Not specified'}`,
  `Routes Processed: ${processedData.routes_taken.join(', ')}`,
  `Content Sources: ${processedData.content_sources.join(', ') || 'None'}`,
  `Sections Available: ${sectionCount}`
].join('\n');

// Add context summary at the beginning
if (sectionCount > 0) {
  processedData.merged_content_for_llm = [
    `## CONTEXT SUMMARY\n${contextSummary}`,
    ...llmContent
  ].join('\n\n---\n\n');
} else {
  // Fallback content if no data was gathered
  if (processedData.topics && processedData.topics.length > 0) {
    const topicsText = Array.isArray(processedData.topics) ? 
      processedData.topics.filter(topic => topic).join('\n- ') : 
      processedData.topics;
    processedData.merged_content_for_llm = `Please generate content about the following topics:\n- ${topicsText}`;
    processedData.content_sources.push('Topic-based Generation Request');
  } else if (processedData.content) {
    processedData.merged_content_for_llm = `Please enhance and format the following content:\n\n${processedData.content}`;
    processedData.content_sources.push('Content Enhancement Request');
  } else {
    processedData.merged_content_for_llm = 'Please generate relevant LinkedIn content based on current trends.';
    processedData.content_sources.push('General Generation Request');
  }
}

// Add processing statistics
processedData.scraping_success_rate = processedData.total_scraped_urls > 0 ? 
  `${((processedData.successful_scrapes / processedData.total_scraped_urls) * 100).toFixed(1)}%` : 'N/A';

// Log final summary
console.log("=== PROCESSING COMPLETE ===");
console.log("Routes taken:", processedData.routes_taken);
console.log("Content sources used:", processedData.content_sources);
console.log("Total content length:", processedData.merged_content_for_llm.length, "characters");
console.log("Sections included:", sectionCount);
console.log("Processing summary:", {
  totalInputs: allInputs.length,
  routesTaken: processedData.routes_taken.length,
  contentSources: processedData.content_sources.length,
  hasScrapedContent: !!processedData.scraped_content,
  hasSearchResults: !!processedData.brave_search_results,
  finalContentLength: processedData.merged_content_for_llm.length
});

return { json: processedData };

