// Process callback query and determine action
const rawCallbackData = $input.item.json.callback_query.data;
console.log('Raw callback data:', rawCallbackData);

// Remove surrounding quotes and clean the callback data
let callbackData = rawCallbackData;
if (typeof callbackData === 'string') {
  // Remove surrounding quotes (both single and double)
  callbackData = callbackData.replace(/^["']+|["']+$/g, '');
  // Remove any remaining quotes inside
  callbackData = callbackData.replace(/["']/g, '');
}

console.log('Cleaned callback data:', callbackData);
const [action, postId_1,postId_2] = callbackData.split('_');
const postId = postId_1 + '_' + postId_2
const queryId = $input.item.json.callback_query.id;
const userId = $input.item.json.callback_query.from.id;
const messageId = $input.item.json.callback_query.message.message_id;
const chatId = $input.item.json.callback_query.message.chat.id;
const userName = $input.item.json.callback_query.from.first_name || 'User';

// Get original post content from message text
const originalText = $input.item.json.callback_query.message.text;

// Extract just the post content (remove any previous instructions)
const contentLines = originalText.split('\n');
let postContent = '';
for (let line of contentLines) {
  if (line.includes('üìã') || line.includes('Actions:') || line.includes('üëÜ')) {
    break;
  }
  postContent += line + '\n';
}
postContent = postContent.trim();

let response = {
  post_id: postId || 'default',
  action: action,
  queryId:queryId,
  user_id: userId,
  chat_id: chatId,
  message_id: messageId,
  user_name: userName,
  original_content: postContent,
  timestamp: new Date().toISOString()
};

switch(action) {
  case 'approve':
    response.status = 'approved';
    response.next_step = 'publish';
    response.linkedin_content = postContent;
    response.message = `‚úÖ Post approved by ${userName}! Publishing to LinkedIn...`;
    break;

  case 'edit':
    response.status = 'editing';
    response.next_step = 'rewrite';
    response.edit_instruction = 'Please improve this post by making it more engaging, professional, and LinkedIn-appropriate. Keep the core message but enhance the tone and structure.';
    response.message = `‚úèÔ∏è ${userName} requested edits. Rewriting with Gemini...`;
    break;

  case 'reject':
    response.status = 'rejected';
    response.next_step = 'complete';
    response.message = `‚ùå Post rejected by ${userName}.`;
    break;

  case 'regenerate':
    response.status = 'regenerating';
    response.next_step = 'regenerate';
    response.regenerate_prompt = 'Create a completely new LinkedIn post on the same topic with a fresh perspective and different approach.';
    response.message = `üîÑ ${userName} requested regeneration. Creating new version...`;
    break;

  case 'schedule':
    response.status = 'scheduling';
