// Get all input items
const items = $input.all();

// Convert everything to a string for searching
const fullDataString = JSON.stringify(items);

// Find ALL URLs in the content
const urlPattern = /https?:\/\/[^\s\"\'<>\(\)\[\]]+/gi;
const allUrls = fullDataString.match(urlPattern) || [];

// Clean and deduplicate URLs
let uniqueUrls = [...new Set(allUrls.map(url => 
  url.replace(/[\(\)\[\]\"\'<>\\]/g, '').trim()
))];

// Function to determine if a URL might be a blog post
function isPotentialBlogPost(url) {
  // Common blog URL patterns
  const blogPatterns = [
    /\/blog\//i,
    /\/post\//i,
    /\/posts\//i,
    /\/article\//i,
    /\/articles\//i,
    /\/news\//i,
    /\/insights\//i,
    /\/stories\//i,
    /\/resources\/blog/i,
    /\/\d{4}\/\d{2}\//,
    /\/how-to-/i,
    /\/guide-to-/i,
    /\/tips/i,
    /\/free-printable-art/i
  ];

  // Exclude patterns
  const excludePatterns = [
    /\.(jpg|jpeg|png|gif|svg|css|js|pdf|zip)$/i,
    /\/wp-content\//i,
    /\/cart\//i,
    /\/checkout/i,
    /\/product\//i,
    /\/shop\//i,
    /\?add-to-cart=/i,
    /\/my-account/i,
    /\/collections?\//i
  ];

  // Check excludes first
  for (const pattern of excludePatterns) {
    if (pattern.test(url)) {
      return false;
    }
  }

  // Check blog patterns
  for (const pattern of blogPatterns) {
    if (pattern.test(url)) {
      return true;
    }
  }

  return false;
}

// Filter URLs
const blogUrls = uniqueUrls.filter(url => url.includes('/blog'));
const potentialBlogPosts = uniqueUrls.filter(isPotentialBlogPost);

// Combine and deduplicate
const allBlogRelatedUrls = [...new Set([...blogUrls, ...potentialBlogPosts])];

// Sort URLs
allBlogRelatedUrls.sort();

// Return results
if (allBlogRelatedUrls.length > 0) {
  return allBlogRelatedUrls.map(url => ({
    json: {
      blogUrl: url
    }
  }));
} else {
  return [{
    json: {
      message: "No blog URLs found",
      totalUrlsChecked: uniqueUrls.length
    }
  }];
}
