const combinedContent =
  items.map(item =>
    item.json.content).join('\n\n');

// Flatten and dedupe citations
const combinedCitations = [
  ...new Set(
    items
      .flatMap(item =>
        item.json.citations || [])
      .filter(url => typeof url ===
        'string' && url.startsWith('http'))
  )
];

return [{
  json: {
    combinedContent,
    combinedCitations
  }
}];

const results = [];

for (const item of items) {
  // Ensure the input item has the expected structure and content
  if (!item.json || !item.json.message || typeof item.json.message.content !== 'string') {
    console.warn("Skipping item: Invalid structure or missing AI content.", item);
    continue;
  }

  const aiResponse = item.json.message.content;

  // Skip placeholder messages if the AI didn't generate actual content
  if (aiResponse.includes("Please provide the collection of relevant stories")) {
    console.log("Skipping instruction/placeholder message from AI.");
    continue;
  }

  const lines = aiResponse.split('\n');

  let currentPackage = {
    text_overlay_output: "",
    video_script_output: "",
    caption_text_output: ""
  };
  let currentSection = ""; // 'overlay', 'script', 'caption'

  for (const line of lines) {
    const trimmedLine = line.trim();

    // Skip empty lines unless they are part of a multi-line content (unlikely for this format)
    if (!trimmedLine && currentSection !== "") {
      // Allow empty lines within content if needed, but for strict format, they usually indicate a break
      // For this specific format, an empty line usually means end of a section if not followed by data
      continue;
    }

    if (trimmedLine.startsWith("Text Overlay:")) {
      // If we've already collected data for a package, push it before starting a new one
      if (currentPackage.text_overlay_output || currentPackage.video_script_output || currentPackage.caption_text_output) {
        results.push({ json: { ...currentPackage } }); // Push a copy of the current package
        currentPackage = { // Reset for the new package
          text_overlay_output: "",
          video_script_output: "",
          caption_text_output: ""
        };
      }
      currentPackage.text_overlay_output = trimmedLine.substring("Text Overlay:".length).trim();
      currentSection = "overlay";
    } else if (trimmedLine.startsWith("Video Script:")) {
      currentPackage.video_script_output = trimmedLine.substring("Video Script:".length).trim();
      currentSection = "script";
    } else if (trimmedLine.startsWith("Caption Text:")) {
      currentPackage.caption_text_output = trimmedLine.substring("Caption Text:".length).trim();
      currentSection = "caption";
    } else {
      // Append content to the current section if it's a continuation
      // Add a space or newline depending on desired handling of multi-line content.
      // Given the prompt's strict output, these are usually single lines.
      // Let's assume continuation means adding a space or newline.
      // For short-form content, it's safer to append with a space if it's not a new key.
      if (currentSection === "video_script_output") {
        currentPackage.video_script_output += (currentPackage.video_script_output ? " " : "") + trimmedLine;
      } else if (currentSection === "caption_text_output") {
        currentPackage.caption_text_output += (currentPackage.caption_text_output ? "\\n" : "") + trimmedLine; // Captions might have line breaks for hashtags etc.
      }
      // No need to append to text_overlay_output as it's typically a single, short line.
    }
  }

  // After the loop, push the last collected package
  if (currentPackage.text_overlay_output || currentPackage.video_script_output || currentPackage.caption_text_output) {
    // Final check for triple quotes in the last script
    if (currentPackage.video_script_output) {
      currentPackage.video_script_output = currentPackage.video_script_output.replace(/\"\"\"/g, "'''");
    }
    results.push({ json: { ...currentPackage } });
  }
}

return results;
