// Server ID aus dem RunComfy Start Server FluxRealism2 Node abrufen
const serverData = $node["RunComfy Start Server"].json;
const serverId = serverData.server_id;

// Loop über alle gefilterten Items und server_id hinzufügen
for (const item of $input.all()) {
  item.json.server_id = serverId;
}

return $input.all();

