// The Get Playlist node returns an array, so we take the first item.
const playlistData = items[0].json;

// Extract the playlist ID, name, and the direct public URL.
const playlistId = playlistData.id;
const playlistName = playlistData.name;
const publicUrl = playlistData.external_urls.spotify;

// Create a simplified array of tracks.
const tracks = playlistData.tracks.items.map(item => {
  return {
    trackName: item.track.name,
    artist: item.track.artists[0].name,
    album: item.track.album.name,
    uri: item.track.uri
  };
});

// Return a new object with the final, simplified structure.
return [{
  json: {
    playlistId,
    playlistName,
    publicUrl,
    tracks
  }
}];
