let combined;

try {
  // 1) grab the segments array
  const segments = $('Generate Timed Script').first().json.message.content.segments;
  if (!Array.isArray(segments)) {
    throw new Error("Expected an array at ('Gnerate Timed Script').first().json.message.content.segments[0]");
  }

  // 2) extract each text and trim whitespace
  const texts = segments.map(s => {
    if (typeof s.text !== "string") {
      throw new Error("Each segment must have a text string");
    }
    return s.text.trim();
  });

  // 3) join into one paragraph (spaces between sentences)
  combined = texts.join(" ");
}
catch (err) {
  throw new Error("Failed to combine segment texts: " + err.message);
}

// 4) return a single item carrying your full script
return [
  {
    json: {
      paragraph: combined
    }
  }
];

