// ========================================
// 📋 MENU CONFIGURATION - PURE NAVIGATION
// ========================================
// This is the ONLY place where menus are defined
// Structure: Simple navigation tree with no business logic
// 
// HOW TO ADD A NEW MENU:
// 1. Add menu key to MENU_CONFIG object
// 2. Define 'text' - what user sees (supports HTML)
// 3. Define 'keyboard' - array of button rows
// 4. Each button needs 'text' (label) and 'callback_data' (routing key)
// 5. Use callback_data for navigation or action routing
// ========================================

const data = items[0].json;

console.log('📋 Loading menu structure...');

// ========================================
// MENU_CONFIG: Central menu definition
// - Key = routing identifier (used in callback_data)
// - Value = menu object with text and keyboard
// ========================================
const MENU_CONFIG = {

  // ==================
  // MAIN MENU - Entry point (Level 0)
  // ==================
  // This is where users land on /start
  main: {
    text: '🏠 <b>MAIN MENU</b>\n\nSelect an option:',
    keyboard: [
      // Row 1: Feature menus
      [
        { text: '📁 Menu 1', callback_data: 'menu1' },  // Navigate to menu1
        { text: '📁 Menu 2', callback_data: 'menu2' }   // Navigate to menu2
      ],
      // Row 2: More features
      [
        { text: '📁 Menu 3', callback_data: 'menu3' },  // Navigate to menu3
        { text: '📁 Menu 4', callback_data: 'menu4' }   // Navigate to menu4
      ],
      // Row 3: System menus
      [
        { text: '⚙️ Settings', callback_data: 'settings' },  // Settings menu
        { text: '❓ Help', callback_data: 'help' }           // Help menu
      ]
    ]
  },

  // ==================
  // MENU 1 - Submenu example (Level 1)
  // ==================
  // Shows how to create actions and nested submenus
  menu1: {
    text: '📁 <b>MENU 1</b>\n\nChoose action:',
    keyboard: [
      // Individual action buttons
      [{ text: '🔸 Action 1.1', callback_data: 'action_1_1' }],  // Triggers action
      [{ text: '🔸 Action 1.2', callback_data: 'action_1_2' }],  // Triggers action
      // Navigate to deeper submenu
      [{ text: '📂 Submenu 1.3', callback_data: 'submenu_1_3' }], // Goes deeper
      // IMPORTANT: Always include navigation back
      [{ text: '🔙 Back', callback_data: 'main' }]  // Returns to main menu
    ]
  },

  // Action endpoints - These trigger business logic in handlers
  // callback_data will be routed to appropriate handler
  action_1_1: {
    text: '✅ Action 1.1 Selected',
    keyboard: [[{ text: '🔙 Back to Menu 1', callback_data: 'menu1' }]]
  },

  action_1_2: {
    text: '✅ Action 1.2 Selected',
    keyboard: [[{ text: '🔙 Back to Menu 1', callback_data: 'menu1' }]]
  },

  // Submenu example (Level 2) - Can go infinitely deep
  submenu_1_3: {
    text: '📂 <b>SUBMENU 1.3</b>\n\nDeeper level:',
    keyboard: [
      [{ text: '▫️ Option A', callback_data: 'option_a' }],     // Action
      [{ text: '▫️ Option B', callback_data: 'option_b' }],     // Action
      [{ text: '🔙 Back to Menu 1', callback_data: 'menu1' }]  // Navigate up
    ]
  },

  option_a: {
    text: '✅ Option A Selected',
    keyboard: [[{ text: '🔙 Back', callback_data: 'submenu_1_3' }]]
  },

  option_b: {
    text: '✅ Option B Selected',
    keyboard: [[{ text: '🔙 Back', callback_data: 'submenu_1_3' }]]
  },

  // ==================
  // MENU 2 - Level 1
  // ==================
  menu2: {
    text: '📁 <b>MENU 2</b>\n\nSelect option:',
    keyboard: [
      [{ text: '🔹 Action 2.1', callback_data: 'action_2_1' }],
      [{ text: '🔹 Action 2.2', callback_data: 'action_2_2' }],
      [{ text: '🔹 Action 2.3', callback_data: 'action_2_3' }],
      [{ text: '🔙 Main Menu', callback_data: 'main' }]
    ]
  },

  action_2_1: {
    text: '✅ Action 2.1 Triggered',
    keyboard: [[{ text: '🔙 Back', callback_data: 'menu2' }]]
  },

  action_2_2: {
    text: '✅ Action 2.2 Triggered',
    keyboard: [[{ text: '🔙 Back', callback_data: 'menu2' }]]
  },

  action_2_3: {
    text: '✅ Action 2.3 Triggered',
    keyboard: [[{ text: '🔙 Back', callback_data: 'menu2' }]]
  },

  // ==================
  // MENU 3 - Level 1
  // ==================
  menu3: {
    text: '📁 <b>MENU 3</b>\n\nPick one:',
    keyboard: [
      [
        { text: '1️⃣', callback_data: 'opt_1' },
        { text: '2️⃣', callback_data: 'opt_2' },
        { text: '3️⃣', callback_data: 'opt_3' },
        { text: '4️⃣', callback_data: 'opt_4' },
        { text: '5️⃣', callback_data: 'opt_5' }
      ],
      [{ text: '🔙 Main', callback_data: 'main' }]
    ]
  },

  opt_1: { text: '✅ Option 1 Selected', keyboard: [[{ text: '🔙', callback_data: 'menu3' }]] },
  opt_2: { text: '✅ Option 2 Selected', keyboard: [[{ text: '🔙', callback_data: 'menu3' }]] },
  opt_3: { text: '✅ Option 3 Selected', keyboard: [[{ text: '🔙', callback_data: 'menu3' }]] },
  opt_4: { text: '✅ Option 4 Selected', keyboard: [[{ text: '🔙', callback_data: 'menu3' }]] },
  opt_5: { text: '✅ Option 5 Selected', keyboard: [[{ text: '🔙', callback_data: 'menu3' }]] },

  // ==================
  // MENU 4 - Level 1
  // ==================
  menu4: {
    text: '📁 <b>MENU 4</b>',
    keyboard: [
      [{ text: '📋 List Items', callback_data: 'list' }],
      [{ text: '➕ Add Item', callback_data: 'add' }],
      [{ text: '✏️ Edit Item', callback_data: 'edit' }],
      [{ text: '🗑 Delete Item', callback_data: 'delete' }],
      [{ text: '🔙 Main', callback_data: 'main' }]
    ]
  },

  list: { text: '📋 Listing items...', keyboard: [[{ text: '🔙', callback_data: 'menu4' }]] },
  add: { text: '➕ Adding item...', keyboard: [[{ text: '🔙', callback_data: 'menu4' }]] },
  edit: { text: '✏️ Editing item...', keyboard: [[{ text: '🔙', callback_data: 'menu4' }]] },
  delete: { text: '🗑 Deleting item...', keyboard: [[{ text: '🔙', callback_data: 'menu4' }]] },

  // ==================
  // SETTINGS - Level 1
  // ==================
  settings: {
    text: '⚙️ <b>SETTINGS</b>',
    keyboard: [
      [{ text: '🔔 Notifications', callback_data: 'notif' }],
      [{ text: '🌍 Language', callback_data: 'lang' }],
      [{ text: '👤 Profile', callback_data: 'profile' }],
      [{ text: '🔐 Privacy', callback_data: 'privacy' }],
      [{ text: '🔙 Main', callback_data: 'main' }]
    ]
  },

  notif: {
    text: '🔔 Notification Settings',
    keyboard: [
      [{ text: 'Enable All', callback_data: 'notif_on' }],
      [{ text: 'Disable All', callback_data: 'notif_off' }],
      [{ text: '🔙 Settings', callback_data: 'settings' }]
    ]
  },

  notif_on: { text: '✅ Notifications Enabled', keyboard: [[{ text: '🔙', callback_data: 'notif' }]] },
  notif_off: { text: '❌ Notifications Disabled', keyboard: [[{ text: '🔙', callback_data: 'notif' }]] },

  lang: {
    text: '🌍 Select Language',
    keyboard: [
      [{ text: '🇬🇧 English', callback_data: 'lang_en' }],
      [{ text: '🇪🇸 Español', callback_data: 'lang_es' }],
      [{ text: '🇫🇷 Français', callback_data: 'lang_fr' }],
      [{ text: '🔙 Settings', callback_data: 'settings' }]
    ]
  },

  lang_en: { text: '✅ English Selected', keyboard: [[{ text: '🔙', callback_data: 'lang' }]] },
  lang_es: { text: '✅ Español Seleccionado', keyboard: [[{ text: '🔙', callback_data: 'lang' }]] },
  lang_fr: { text: '✅ Français Sélectionné', keyboard: [[{ text: '🔙', callback_data: 'lang' }]] },

  profile: { text: '👤 Profile Settings', keyboard: [[{ text: '🔙', callback_data: 'settings' }]] },
  privacy: { text: '🔐 Privacy Settings', keyboard: [[{ text: '🔙', callback_data: 'settings' }]] },

  // ==================
  // HELP - Level 1
  // ==================
  help: {
    text: '❓ <b>HELP</b>',
    keyboard: [
      [{ text: '📖 Guide', callback_data: 'guide' }],
      [{ text: '❓ FAQ', callback_data: 'faq' }],
      [{ text: '📞 Contact', callback_data: 'contact' }],
      [{ text: '🔙 Main', callback_data: 'main' }]
    ]
  },

  guide: { text: '📖 User Guide', keyboard: [[{ text: '🔙', callback_data: 'help' }]] },
  faq: { text: '❓ FAQ', keyboard: [[{ text: '🔙', callback_data: 'help' }]] },
  contact: { text: '📞 Contact Support', keyboard: [[{ text: '🔙', callback_data: 'help' }]] },

  // ==================
  // DEFAULT FALLBACK - IMPORTANT!
  // ==================
  // This menu is shown when callback_data doesn't match any menu key
  default: {
    text: '❓ Unknown selection',
    keyboard: [[{ text: '🏠 Main Menu', callback_data: 'main' }]]
  }
};

// ========================================
// MENU STRUCTURE RULES:
// 1. Each menu MUST have 'text' and 'keyboard'
// 2. Text supports HTML: <b>bold</b>, <i>italic</i>, <code>code</code>
// 3. Use \n for line breaks in text
// 4. Keyboard is array of arrays (rows of buttons)
// 5. Each button MUST have 'text' (display) and 'callback_data' (routing)
// 6. callback_data should match a menu key or be handled by router
// 7. Always provide navigation back to parent menu
// ========================================

console.log(`✅ Menu structure loaded: ${Object.keys(MENU_CONFIG).length} menus`);

// Pass menu config with original data
return [{
  json: {
    ...data,
    menuConfig: MENU_CONFIG
  }
}];

// Extract command and user data

const data = items[0].json;
let command = '';
let userName = '';
let userId = '';
let chatId = '';
let messageId = '';
let isCallback = false;

// Extract data
if (data.message) {
  const message = data.message;
  chatId = message.chat.id;
  userId = message.from.id;
  userName = message.from.first_name || 'User';

  const text = (message.text || '').toLowerCase();
  if (text.startsWith('/start')) command = 'main';
  else if (text.includes('help')) command = 'help';
  else if (text.includes('settings')) command = 'settings';
  else if (text.includes('stats')) command = 'stats';
  else if (text.includes('feedback')) command = 'feedback';
  else command = 'main';

} else if (data.callback_query) {
  const callback = data.callback_query;
  chatId = callback.message.chat.id;
  messageId = callback.message.message_id;
  userId = callback.from.id;
  userName = callback.from.first_name || 'User';
  isCallback = true;
  command = callback.data;
}

console.log(`Command: '${command}', User: ${userName}`);

return [{
  json: {
    ...data,
    command: command,
    userName: userName,
    userId: userId,
    chatId: chatId,
    messageId: messageId,
    isCallback: isCallback,
    callbackQueryId: data.callback_query ? data.callback_query.id : null
  }
}];

// Process command and check if action needed

const input = items[0].json;

// Get menu
let menu = input.menuConfig[input.command] || input.menuConfig.default;

// Commands that need actions
const actionCommands = [
  'rate_1', 'rate_2', 'rate_3', 'rate_4', 'rate_5',
  'lang_en', 'lang_es',
  'analytics', 'report',
  'stats', 'achievements',
  'bug_report', 'feature_request',
  'notif', 'profile'
];

const requiresAction = actionCommands.includes(input.command);

console.log(`Processing: ${input.command}, Action needed: ${requiresAction}`);

return [{
  json: {
    ...input,
    menu: menu,
    requiresAction: requiresAction
  }
}];

// Rating Handler - Process ratings

const input = items[0].json;
const rating = parseInt(input.command.split('_')[1]);

const messages = {
  1: 'We\'re sorry to hear that. We\'ll improve!',
  2: 'Thank you for the feedback. We\'ll do better!',
  3: 'Good to know! We\'re always improving.',
  4: 'Great! We\'re glad you liked it!',
  5: 'Awesome! Thank you so much!'
};

console.log(`⭐ Rating saved: ${rating}`);

return [{
  json: {
    ...input,
    customData: {
      ratingMessage: messages[rating]
    },
    actionExecuted: 'rating_saved'
  }
}];

// Language Handler - Change language

const input = items[0].json;
const lang = input.command.split('_')[1];

console.log(`🌍 Language changed to: ${lang}`);

return [{
  json: {
    ...input,
    customData: {},
    actionExecuted: 'language_changed'
  }
}];

// Analytics Handler - Generate analytics/reports

const input = items[0].json;
let customData = {};

if (input.command === 'analytics') {
  const views = Math.floor(Math.random() * 1000) + 100;
  const clicks = Math.floor(Math.random() * 500) + 50;
  customData.analyticsData = `Views: ${views}\nClicks: ${clicks}\nCTR: ${(clicks/views*100).toFixed(2)}%`;
  console.log('📈 Analytics generated');
} else if (input.command === 'report') {
  customData.reportData = `Report ID: RPT-${Date.now()}\nPeriod: Last 30 days\nStatus: Ready`;
  console.log('📄 Report generated');
}

return [{
  json: {
    ...input,
    customData: customData,
    actionExecuted: 'analytics_generated'
  }
}];

// Statistics Handler - Load stats/achievements

const input = items[0].json;
let customData = {};

if (input.command === 'stats') {
  customData.statsData = `Total actions: ${Math.floor(Math.random() * 100)}\nActive days: ${Math.floor(Math.random() * 30)}\nRatings given: ${Math.floor(Math.random() * 10)}`;
  console.log('📊 Stats loaded');
} else if (input.command === 'achievements') {
  customData.achievementData = '🥇 Early Adopter\n🥈 Active User\n🥉 Feedback Champion';
  console.log('🏆 Achievements loaded');
}

return [{
  json: {
    ...input,
    customData: customData,
    actionExecuted: 'stats_loaded'
  }
}];

// Feedback Handler - Create tickets

const input = items[0].json;
const ticketType = input.command === 'bug_report' ? 'BUG' : 'REQ';
const ticketId = `${ticketType}-${Date.now()}`;

const customData = {
  ticketInfo: `Ticket created: ${ticketId}\nStatus: Open\nPriority: Medium`
};

console.log(`🎫 Ticket created: ${ticketId}`);

return [{
  json: {
    ...input,
    customData: customData,
    actionExecuted: 'ticket_created'
  }
}];

// Settings Handler - Load settings data

const input = items[0].json;
let customData = {};

if (input.command === 'notif') {
  customData.notificationStatus = '✅ Push: ON\n✅ Email: ON\n❌ SMS: OFF';
  console.log('🔔 Notifications loaded');
} else if (input.command === 'profile') {
  customData.userStatus = 'Active';
  console.log('👤 Profile loaded');
}

return [{
  json: {
    ...input,
    customData: customData,
    actionExecuted: 'settings_loaded'
  }
}];

// Default Handler - No specific action

const input = items[0].json;

console.log(`ℹ️ No action for: ${input.command}`);

return [{
  json: {
    ...input,
    customData: {},
    actionExecuted: 'none'
  }
}];

// Build final response with data

const input = items[0].json;
const menu = input.menu;

// Replace placeholders
let responseText = menu.text
  .replace(/{userName}/g, input.userName)
  .replace(/{userId}/g, input.userId);

// Replace custom data
if (input.customData) {
  for (const [key, value] of Object.entries(input.customData)) {
    responseText = responseText.replace(new RegExp(`{${key}}`, 'g'), value);
  }
}

// Clean remaining placeholders
responseText = responseText.replace(/{\w+}/g, '');

console.log(`📤 Sending: ${input.command}`);
if (input.actionExecuted) {
  console.log(`Action: ${input.actionExecuted}`);
}

return [{
  json: {
    ...input,
    responseText: responseText,
    keyboard: menu.keyboard
  }
}];

// Prepare Telegram request

const input = items[0].json;

const apiMethod = input.isCallback ? 'editMessageText' : 'sendMessage';

let requestBody = {
  chat_id: input.chatId,
  text: input.responseText,
  parse_mode: 'HTML',
  reply_markup: {
    inline_keyboard: input.keyboard
  }
};

if (input.isCallback && input.messageId) {
  requestBody.message_id = input.messageId;
}

return [{
  json: {
    apiMethod: apiMethod,
    requestBody: requestBody,
    isCallback: input.isCallback,
    callbackQueryId: input.callbackQueryId
  }
}];
