// Clear chat history for this user
const userId = $input.item.json.message.from.id;
const chatId = $input.item.json.message.chat.id;

// Clear the chat history for this user
delete $getWorkflowStaticData('global')[`chat_${userId}`];

return {
  userId: userId,
  chatId: chatId,
  cleared: true
};

// Get user ID and current message
const userId = $input.item.json.message.from.id;
const userMessage = $input.item.json.message.text;
const chatId = $input.item.json.message.chat.id;

// Initialize or get existing chat history
let chatHistory = $getWorkflowStaticData('global')[`chat_${userId}`] || [];

// Add user message to history
chatHistory.push({
  role: "user",
  content: userMessage
});

// Keep only last 10 messages (5 exchanges) to manage token limits
if (chatHistory.length > 10) {
  chatHistory = chatHistory.slice(-10);
}

// Save updated history
$getWorkflowStaticData('global')[`chat_${userId}`] = chatHistory;

return {
  userId: userId,
  chatId: chatId,
  currentMessage: userMessage,
  chatHistory: chatHistory
};

// Prepare the messages array with system message + chat history
const systemMessage = {
  role: "system",
  content: "You are a helpful Coddzz AI assistant for a Telegram bot. Be concise, friendly, and helpful. Keep responses under 200 words unless specifically asked for longer explanations."
};

const chatHistory = $input.item.json.chatHistory || [];
const messages = [systemMessage, ...chatHistory];

const requestBody = {
  model: "mistralai/mistral-nemotron",
  messages: messages,
  temperature: 0.6,
  top_p: 0.7,
  frequency_penalty: 0,
  presence_penalty: 0,
  max_tokens: 4096,
  stream: false
};

return {
  requestBody: requestBody,
  chatHistory: chatHistory
};

// Get the AI response
const aiResponse = $input.item.json.choices[0].message.content;
const userId = $('Manage Chat History').item.json.userId;

// Get current chat history
let chatHistory = $getWorkflowStaticData('global')[`chat_${userId}`] || [];

// Add AI response to history
chatHistory.push({
  role: "assistant",
  content: aiResponse
});

// Keep only last 10 messages
if (chatHistory.length > 10) {
  chatHistory = chatHistory.slice(-10);
}

// Save updated history
$getWorkflowStaticData('global')[`chat_${userId}`] = chatHistory;

return {
  aiResponse: aiResponse,
  userId: userId,
  chatHistory: chatHistory
};
