// n8n Code node â€” Run Once for All Items
// Computes RSI(14) (Wilder) on 1h candles and raises 30/70 cross alerts.

const PERIOD = 14;
const OVERBOUGHT = 70;
const OVERSOLD = 30;

// Collect ALL candles from the HTTP node (1 item = 1 candle)
const inputItems = $input.all();
let candles = [];
for (const it of inputItems) {
  if (Array.isArray(it.json)) candles.push(...it.json);
  else candles.push(it.json);
}

// Normalize + sort by time (prefer numeric timestamp, fallback to datetime)
candles = candles
  .filter(r => r && r.close !== undefined)
  .map(r => ({
    t: (Number.isFinite(+r.timestamp) ? +r.timestamp
       : (typeof r.datetime === 'number' ? r.datetime : Date.parse(r.datetime)/1000)),
    close: +r.close
  }))
  .filter(r => Number.isFinite(r.t) && Number.isFinite(r.close))
  .sort((a,b) => a.t - b.t);

if (candles.length < PERIOD + 2) {
  return [{ json: { error: 'Not enough candles for RSI', count: candles.length } }];
}

const closes = candles.map(c => c.close);

// Wilder RSI (full series)
function rsiSeries(values, period = 14) {
  const deltas = [];
  for (let i = 1; i < values.length; i++) deltas.push(values[i] - values[i - 1]);
  let gain = 0, loss = 0;
  for (let i = 0; i < period; i++) { const d = deltas[i]; if (d >= 0) gain += d; else loss -= d; }
  let avgGain = gain / period; let avgLoss = loss / period;
  const rsis = new Array(values.length).fill(null);
  rsis[period] = avgLoss === 0 ? 100 : 100 - (100 / (1 + (avgGain / avgLoss)));
  for (let i = period + 1; i < values.length; i++) {
    const d = deltas[i - 1];
    const up = Math.max(d, 0);
    const down = Math.max(-d, 0);
    avgGain = ((avgGain * (period - 1)) + up) / period;
    avgLoss = ((avgLoss * (period - 1)) + down) / period;
    const rs = avgLoss === 0 ? Infinity : (avgGain / avgLoss);
    rsis[i] = 100 - (100 / (1 + rs));
  }
  return rsis;
}

const rsis = rsiSeries(closes, PERIOD);
const lastIdx = rsis.length - 1;
const rsiNow = +rsis[lastIdx].toFixed(1);
const rsiPrev = +rsis[lastIdx - 1].toFixed(1);
const lastClose = +closes[lastIdx].toFixed(2);
const lastTs = candles[lastIdx].t;

// Signals
let signal = null;
if (rsiPrev > OVERSOLD && rsiNow <= OVERSOLD) signal = 'enter_oversold';
else if (rsiPrev < OVERBOUGHT && rsiNow >= OVERBOUGHT) signal = 'enter_overbought';
else if (rsiPrev <= OVERSOLD && rsiNow > OVERSOLD) signal = 'exit_oversold';
else if (rsiPrev >= OVERBOUGHT && rsiNow < OVERBOUGHT) signal = 'exit_overbought';

// ======= TEST TOGGLE (set true only to test Telegram delivery) =======
const FORCE_ALERT  = false;                 // keep false in production
const FORCE_SIGNAL = 'enter_overbought';    // 'enter_oversold' | 'exit_oversold' | 'exit_overbought'
if (FORCE_ALERT) signal = FORCE_SIGNAL;
// =====================================================================

// Current symbol from the loop item (fallback to input $json)
const symbol = $('Split Out')?.item?.json?.symbol || $json.symbol || 'UNKNOWN';

const TF = '1h';
const fmt = (n, d=2) => Number(n).toLocaleString('en-US',{minimumFractionDigits:d, maximumFractionDigits:d});
const tsUTC = (ts) => new Date(ts*1000).toISOString().replace('T',' ').slice(0,16) + ' UTC';

let emoji = 'ðŸ””', headline = '';
if (signal === 'enter_oversold')        { emoji='ðŸ”»'; headline = `enters <u>oversold</u> (RSI ${rsiNow} â‰¤ 30)`; }
else if (signal === 'enter_overbought') { emoji='ðŸš€'; headline = `enters <u>overbought</u> (RSI ${rsiNow} â‰¥ 70)`; }
else if (signal === 'exit_oversold')    { emoji='âœ…'; headline = `exits <u>oversold</u> (RSI ${rsiNow})`; }
else if (signal === 'exit_overbought')  { emoji='âœ…'; headline = `exits <u>overbought</u> (RSI ${rsiNow})`; }

const alertTextHtml = signal ? (
  `${emoji} <b>${symbol}</b> ${headline}
` +
  `Price: <b>$${fmt(lastClose)}</b> Â· TF: <b>${TF}</b> Â· ${tsUTC(lastTs)}
` +
  `RSI: <b>${rsiPrev} â†’ ${rsiNow}</b> (30/70)
` +
  `â€” <i>RSI Heatwave</i>`
) : null;

const alertText = signal ? `${symbol} | ${headline.replace(/<[^>]*>/g,'')} | Price $${fmt(lastClose)} Â· TF ${TF} Â· ${tsUTC(lastTs)} | RSI ${rsiPrev}â†’${rsiNow}` : null;

// TradingView link (BINANCE + USD) from EODHD symbol (e.g., BTC-USD.CC â†’ BTCUSD)
const rawSymbol = $('Split Out')?.item?.json?.symbol ?? $json.symbol ?? symbol;
const sym  = Array.isArray(rawSymbol) ? rawSymbol[0] : rawSymbol; 
const base = String(sym).split('-')[0].toUpperCase();
const tradingViewUrl = `https://www.tradingview.com/symbols/${base}USD/?exchange=BINANCE`;

return [{ json: { symbol, rsi: rsiNow, rsiPrev, period: PERIOD, lastClose, signal, timestamp: lastTs, alertText, alertTextHtml, tradingViewUrl } }];
