// Generate professional email HTML format
let htmlOutput = '';

for (const item of $input.all()) {
    // Professional email template
    htmlOutput += `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <style>
        body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; margin: 0; padding: 20px; }
        .email-container { max-width: 600px; margin: 0 auto; background: #ffffff; border: 1px solid #e0e0e0; border-radius: 8px; overflow: hidden; }
        .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px 20px; text-align: center; }
        .header h1 { margin: 0; font-size: 24px; font-weight: 300; }
        .content { padding: 30px; }
        .greeting { font-size: 16px; margin-bottom: 20px; }
        .info-section { background: #f8f9fa; border-radius: 6px; padding: 20px; margin: 20px 0; }
        .info-title { font-size: 18px; font-weight: 600; color: #495057; margin-bottom: 15px; border-bottom: 2px solid #dee2e6; padding-bottom: 8px; }
        .info-row { display: flex; margin: 12px 0; padding: 8px 0; border-bottom: 1px solid #e9ecef; }
        .info-row:last-child { border-bottom: none; }
        .info-label { font-weight: 600; color: #6c757d; min-width: 180px; }
        .info-value { color: #495057; flex: 1; }
        .footer { background: #f8f9fa; padding: 20px; text-align: center; font-size: 14px; color: #6c757d; border-top: 1px solid #e0e0e0; }
        .signature { margin-top: 30px; padding-top: 20px; border-top: 1px solid #dee2e6; }
    </style>
</head>
<body>
    <div class="email-container">
        <div class="header">
            <h1>New Customer Inquiry</h1>
        </div>

        <div class="content">
            <div class="greeting">
                Dear Team,
            </div>

            <p>We have received a new customer inquiry through our contact form. Please find the details below and ensure appropriate follow-up action is taken.</p>

            <div class="info-section">
                <div class="info-title">Customer Details</div>`;

    // Loop through each key-value pair in the JSON object
    for (const [key, value] of Object.entries(item.json)) {
        htmlOutput += `
                <div class="info-row">
                    <div class="info-label">${key}:</div>
                    <div class="info-value">${value}</div>
                </div>`;
    }

    htmlOutput += `
            </div>

            <p><strong>Next Steps:</strong></p>
            <ul>
                <li>Acknowledge receipt within 2 business hours</li>
                <li>Assign to appropriate team member based on product/service inquiry</li>
                <li>Schedule follow-up call if urgency is high</li>
                <li>Update CRM system with customer information</li>
            </ul>

            <div class="signature">
                <p>Best regards,<br>
                <strong>Customer Service Team</strong><br>
                Email: support@company.com<br>
                Phone: +1 (555) 123-4567</p>
            </div>
        </div>

        <div class="footer">
            <p>This is an automated notification. Please do not reply to this email.</p>
            <p>&copy; 2024 Your Company Name. All rights reserved.</p>
        </div>
    </div>
</body>
</html>`;
}

// Return the professional email HTML
return [{ 
    json: {
        html: htmlOutput,
        htmlContent: htmlOutput,
        itemCount: $input.all().length,
        emailSubject: "New Customer Inquiry - Action Required"
    }
}];

// ClickUp Data Formatter for Google Sheets
// This code formats ClickUp task data into a structured format for spreadsheet logging

const inputData = $input.all();
const formattedData = [];

for (const item of inputData) {
  const taskData = item.json;

  // Format dates for better readability
  const formatDate = (timestamp) => {
    if (!timestamp) return '';
    return new Date(parseInt(timestamp)).toLocaleString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      timeZone: 'UTC'
    });
  };

  // Extract assignee names
  const getAssignees = (assignees) => {
    if (!assignees || assignees.length === 0) return 'Unassigned';
    return assignees.map(assignee => assignee.username).join(', ');
  };

  // Extract watcher names
  const getWatchers = (watchers) => {
    if (!watchers || watchers.length === 0) return '';
    return watchers.map(watcher => watcher.username).join(', ');
  };

  // Extract tags
  const getTags = (tags) => {
    if (!tags || tags.length === 0) return '';
    return tags.map(tag => tag.name).join(', ');
  };

  // Extract custom fields
  const getCustomFields = (customFields) => {
    if (!customFields || customFields.length === 0) return '';
    return customFields.map(field => `${field.name}: ${field.value}`).join(' | ');
  };

  // Format priority
  const getPriority = (priority) => {
    if (!priority) return 'None';
    return priority.priority || 'None';
  };

  // Calculate time estimates and spent in hours
  const formatTime = (timeInMs) => {
    if (!timeInMs || timeInMs === 0) return '0';
    return (timeInMs / (1000 * 60 * 60)).toFixed(2); // Convert to hours
  };

  // Format the data for Google Sheets
  const formattedItem = {
    // Basic Task Information
    'Task ID': taskData.id || '',
    'Task Name': taskData.name || '',
    'Description': taskData.description || taskData.text_content || '',
    'Status': taskData.status?.status || '',
    'Status Color': taskData.status?.color || '',
    'Priority': getPriority(taskData.priority),

    // Dates
    'Date Created': formatDate(taskData.date_created),
    'Date Updated': formatDate(taskData.date_updated),
    'Date Closed': formatDate(taskData.date_closed),
    'Date Done': formatDate(taskData.date_done),
    'Due Date': formatDate(taskData.due_date),
    'Start Date': formatDate(taskData.start_date),

    // People
    'Creator': taskData.creator?.username || '',
    'Creator Email': taskData.creator?.email || '',
    'Assignees': getAssignees(taskData.assignees),
    'Watchers': getWatchers(taskData.watchers),

    // Organization
    'List Name': taskData.list?.name || '',
    'List ID': taskData.list?.id || '',
    'Project Name': taskData.project?.name || '',
    'Project ID': taskData.project?.id || '',
    'Folder Name': taskData.folder?.name || '',
    'Space ID': taskData.space?.id || '',
    'Team ID': taskData.team_id || '',

    // Task Details
    'Points': taskData.points || 0,
    'Time Estimate (Hours)': formatTime(taskData.time_estimate),
    'Time Spent (Hours)': formatTime(taskData.time_spent),
    'Order Index': taskData.orderindex || '',
    'Archived': taskData.archived ? 'Yes' : 'No',

    // Additional Information
    'Tags': getTags(taskData.tags),
    'Custom Fields': getCustomFields(taskData.custom_fields),
    'Has Dependencies': taskData.dependencies && taskData.dependencies.length > 0 ? 'Yes' : 'No',
    'Has Linked Tasks': taskData.linked_tasks && taskData.linked_tasks.length > 0 ? 'Yes' : 'No',
    'Has Checklists': taskData.checklists && taskData.checklists.length > 0 ? 'Yes' : 'No',

    // URLs and Sharing
    'Task URL': taskData.url || '',
    'Public Sharing': taskData.sharing?.public ? 'Yes' : 'No',
    'Permission Level': taskData.permission_level || '',

    // Metadata
    'Data Processed At': new Date().toISOString(),
    'Processing Status': 'Success'
  };

  formattedData.push({
    json: formattedItem,
    pairedItem: item.pairedItem
  });
}

// Return the formatted data
return formattedData;
