function stripMarkdownJson(input) {
  let str = (input || '').trim();

  // Remove the ```json at the start and ```
  // This regex removes triple backticks and the 'json' keyword at the start
  str = str.replace(/^```json\s*/, '');  // Remove starting ```
  str = str.replace(/```$/, '');          // Remove ending ```

  return str.trim();
}

function normalizeRole(role) {
  return (role || '').trim().toLowerCase();
}

const roleToManagerEmail = {
  'java team lead': 'javatl@mailinator.com',
  'python developer': 'pythontl@mailinator.com',
  'frontend developer': 'frontendtl@mailinator.com',
  // add other roles as needed
};

const fallbackManagerEmail = 'fallback@mailinator.com';

return items.map(item => {
  const raw = item.json.text || '';
  const jsonStr = stripMarkdownJson(raw);

  let parsed;
  try {
    parsed = JSON.parse(jsonStr);
  } catch (e) {
    item.json.error = `JSON parse failed: ${e.message}`;
    return item;
  }

  // Merge the parsed JSON fields into the current item
  Object.assign(item.json, parsed);

  // Normalize role and assign manager email
  const normalizedRole = normalizeRole(item.json.role);
  const managerEmail = Object.prototype.hasOwnProperty.call(roleToManagerEmail, normalizedRole)
    ? roleToManagerEmail[normalizedRole]
    : fallbackManagerEmail;

  item.json.managerEmail = managerEmail;

  return item;
});

function escapeHtml(text) {
  if (!text) return '';
  return text
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#039;");
}

// Group applicants by managerEmail, then by role
const groupedByManager = {};

// Step 1: Group items
items.forEach(item => {
  const applicant = item.json;
  const managerEmail = applicant.managerEmail || 'unknown@fallback.com';
  const role = applicant.role || 'Unknown Role';

  if (!groupedByManager[managerEmail]) {
    groupedByManager[managerEmail] = {};
  }
  if (!groupedByManager[managerEmail][role]) {
    groupedByManager[managerEmail][role] = [];
  }

  groupedByManager[managerEmail][role].push(applicant);
});

// Step 2: Build HTML per manager with tables per role
const output = [];

for (const [managerEmail, roles] of Object.entries(groupedByManager)) {

  let emailHtml = `<h2>Today's New Applicants</h2>`;

  for (const [role, applicants] of Object.entries(roles)) {
    emailHtml += `<h3>Role: ${escapeHtml(role)}</h3>`;

    // Build table header
    emailHtml += `
    <table border="1" cellpadding="5" cellspacing="0" style="border-collapse:collapse; width: 100%;">
      <thead style="background-color:#f0f0f0;">
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>Phone</th>
          <th>Years of Experience</th>
          <th>Top Skills</th>
          <th>Location</th>
          <th>Notice Period</th>
          <th>Summary</th>
        </tr>
      </thead>
      <tbody>
    `;

    // Add each applicant as a row
    applicants.forEach(app => {
      emailHtml += `
        <tr>
          <td>${escapeHtml(app.name)}</td>
          <td><a href="mailto:${escapeHtml(app.email)}">${escapeHtml(app.email)}</a></td>
          <td>${escapeHtml(app.phone)}</td>
          <td>${escapeHtml(app.years_of_experience?.toString())}</td>
          <td>${Array.isArray(app.top_skills) ? escapeHtml(app.top_skills.join(', ')) : escapeHtml(app.top_skills)}</td>
          <td>${escapeHtml(app.location)}</td>
          <td>${escapeHtml(app.notice_period)}</td>
          <td>${escapeHtml(app.summary)}</td>
        </tr>
      `;
    });

    emailHtml += `</tbody></table><br/>`;
  }

  // Push one item per manager, as output for the next step (email sending)
  output.push({
    json: {
      managerEmail,
      html: emailHtml
    }
  });
}

return output;
