// Helper to parse various local date formats to UTC Date object in Asia/Kolkata timezone
// Supports formats like DD/MM/YYYY, DD-MM-YYYY, MM/DD/YYYY, and ISO strings
function parseDateToIST(dateStr) {
  if (!dateStr) return null;

  // Remove extra spaces and check if ISO first
  dateStr = dateStr.trim();
  const isoMatch = dateStr.match(/^\d{4}-\d{2}-\d{2}/);
  if (isoMatch) {
    // ISO string, parse directly
    return new Date(dateStr);
  }

  // Handle DD/MM/YYYY or DD-MM-YYYY or MM/DD/YYYY formats by heuristics
  // We assume Indian locale DD/MM/YYYY by default
  // We'll try parsing with a few patterns; fallback to null if fails
  const parts1 = dateStr.split(/[\/\-]/);
  if (parts1.length !== 3) return null;

  let dd, mm, yyyy;
  // Heuristic to detect if third part is year or not
  if (parts1[2].length === 4) {
    // Probably DD/MM/YYYY or MM/DD/YYYY
    // Let's assume DD/MM/YYYY (Indian standard)
    dd = parseInt(parts1[0], 10);
    mm = parseInt(parts1[1], 10) - 1;  // month is zero-based in JS Date
    yyyy = parseInt(parts1[2], 10);
  } else {
    return null;
  }

  // Build Date in IST timezone by creating UTC equivalent adjusted for IST offset
  // IST is UTC+05:30, so build Date in UTC and add offset
  // To avoid confusion, create Date in UTC and adjust by offset
  const date = new Date(Date.UTC(yyyy, mm, dd));
  return date;
}

// Get today's date in IST (without time, for comparison)
function getTodayIST() {
  const nowUTC = new Date();
  // IST offset in minutes
  const offsetMinutes = 5 * 60 + 30;
  // convert to IST
  const nowIST = new Date(nowUTC.getTime() + offsetMinutes * 60 * 1000);
  // Zero out time fields for date-only comparison
  return new Date(nowIST.getFullYear(), nowIST.getMonth(), nowIST.getDate());
}

// Calculate difference in full days between two dates
function daysBetween(date1, date2) {
  if (!(date1 instanceof Date) || !(date2 instanceof Date)) return null;
  const msPerDay = 24 * 60 * 60 * 1000;
  const utc1 = Date.UTC(date1.getFullYear(), date1.getMonth(), date1.getDate());
  const utc2 = Date.UTC(date2.getFullYear(), date2.getMonth(), date2.getDate());
  return Math.floor((utc2 - utc1) / msPerDay);
}

const todayIST = getTodayIST();
const staleThresholdDays = 3;  // Adjust as per config if needed

return items.map(item => {
  // Normalize keys to lowercase for consistency:
  const normalized = {};
  for (const key in item.json) {
    normalized[key.toLowerCase()] = item.json[key];
  }

  // Parse dates
  const completionDateRaw = normalized['bgv_completion_date'];
  const lastFollowUpRaw = normalized['last_follow_up'];

  const completionDate = parseDateToIST(completionDateRaw);
  const lastFollowUpDate = parseDateToIST(lastFollowUpRaw);

  // Determine if Completed Today (completed and completionDate == today IST)
  const isCompleted = normalized['bgv_status'] === 'Completed';
  const isCompletedToday = isCompleted && completionDate &&
    daysBetween(completionDate, todayIST) === 0;

  // Calculate days since last follow up (null if no date)
  const daysSinceFollowUp = lastFollowUpDate ? daysBetween(lastFollowUpDate, todayIST) : null;

  // Is stale if last follow up was >= staleThresholdDays ago (and status Pending)
  const isPending = normalized['bgv_status'] === 'Pending';
  const isStale = isPending && daysSinceFollowUp !== null && daysSinceFollowUp >= staleThresholdDays;

  return {
    json: {
      ...normalized,
      bgv_completion_date_parsed: completionDate ? completionDate.toISOString().slice(0,10) : null,
      last_follow_up_parsed: lastFollowUpDate ? lastFollowUpDate.toISOString().slice(0,10) : null,
      isCompletedToday,
      daysSinceFollowUp,
      isStale,
    }
  };
});

// Input items assumed to have these properties already calculated in the previous node:
// - bgv_exe_email (string)
// - isCompletedToday (boolean)
// - bgv_status (string)
// - isStale (boolean)

// Create a map keyed by bgv_exe_email
const grouped = {};

// Iterate over each item from previous node
items.forEach(item => {
  const json = item.json;
  const exeEmail = json['bgv_exe_email'];

  if (!exeEmail) {
    // Skip rows without executive email (or could collect in a "no-exe" group if desired)
    return;
  }

  if (!grouped[exeEmail]) {
    grouped[exeEmail] = {
      bgv_exe_email: exeEmail,
      completedToday: [],
      pending: []
    };
  }

  // Check conditions
  if (json.isCompletedToday) {
    grouped[exeEmail].completedToday.push(json);
  } 
  else if (json.bgv_status === 'Pending' && json.bgv_status !== 'To be Sent') {
    grouped[exeEmail].pending.push(json);
  }
});

// For each group, add the stale flags (already on rows), so no need to add extra here

// Prepare output array, one item per executive with grouped data
const output = Object.values(grouped).map(group => {
  return {
    json: group
  };
});

return output;

// Helper: Escape HTML to avoid injection issues
function escapeHtml(text) {
  if (!text) return '';
  return text.toString()
    .replace(/&/g, "&amp;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;");
}

// Template configuration (can be replaced with variables or data from config node)
const subjectTemplate = (date, completedCount, pendingCount) =>
  `BGV digest for ${date} — ${completedCount} completed, ${pendingCount} pending`;

// For date display in subject and intro - IST date as YYYY-MM-DD
const todayIST = new Date();
todayIST.setHours(todayIST.getHours() + 5);
todayIST.setMinutes(todayIST.getMinutes() + 30);
const yyyy = todayIST.getFullYear();
const mm = String(todayIST.getMonth() + 1).padStart(2, '0');
const dd = String(todayIST.getDate()).padStart(2, '0');
const todayStr = `${yyyy}-${mm}-${dd}`;

// Function to build a HTML table for a list of candidates
function buildTable(rows, includeStale=false) {
  if (!rows || rows.length === 0) {
    return '<p><i>None</i></p>';
  }
  let html = '<table border="1" cellpadding="5" cellspacing="0" style="border-collapse: collapse;">';
  html += `
    <thead>
      <tr>
        <th>Candidate Name</th>
        <th>Previous Company</th>
        <th>Previous HR</th>
        <th>Previous HR Email</th>
        ${includeStale ? '<th>Last Follow-up</th><th>Status</th>' : ''}
      </tr>
    </thead>
    <tbody>
  `;

  rows.forEach(row => {
    html += '<tr>';
    html += `<td>${escapeHtml(row.candidate_name)}</td>`;
    html += `<td>${escapeHtml(row.previous_company)}</td>`;
    html += `<td>${escapeHtml(row.prevco_hr_name)}</td>`;
    html += `<td><a href="mailto:${escapeHtml(row.prevco_hr_email)}">${escapeHtml(row.prevco_hr_email)}</a></td>`;
    if (includeStale) {
      const lastFollowUpDisplay = row.last_follow_up_parsed || '';
      const statusDisplay = escapeHtml(row.bgv_status) + (row.isStale ? ' ⚠️' : '');
      html += `<td>${lastFollowUpDisplay}</td>`;
      html += `<td>${statusDisplay}</td>`;
    }
    html += '</tr>';
  });

  html += '</tbody></table>';
  return html;
}

// Array to hold output per executive for SMTP node
return items.map(item => {
  const execEmail = item.json.bgv_exe_email;
  const completed = item.json.completedToday || [];
  const pending = item.json.pending || [];

  const completedCount = completed.length;
  const pendingCount = pending.length;

  // Compose email subject
  const subject = subjectTemplate(todayStr, completedCount, pendingCount);

  // Compose the HTML body content
  const intro = `<p>Dear Executive,</p><p>Here is your daily background verification digest for <b>${todayStr}</b>.</p>`;

  const completedSection = `
    <h2>Completed Today (${completedCount})</h2>
    ${buildTable(completed, false)}
  `;

  const pendingSection = `
    <h2>Pending (${pendingCount})</h2>
    ${buildTable(pending, true)}
    <p><small>⚠️ indicates stale pending items (no follow-up in last 3 days)</small></p>
  `;

  const outro = `<p>Regards,<br/>BGV Operations Team</p>`;

  const htmlBody = `
    <html>
    <body>
      ${intro}
      ${completedSection}
      ${pendingSection}
      ${outro}
    </body>
    </html>
  `;

  return {
    json: {
      to: execEmail,
      subject: subject,
      html: htmlBody
    }
  };
});
