const allPlayerIds = new Set();
const allGames = items[0].json.dates[0]?.games || [];

if (allGames.length === 0) {
  return [];
}

// Loop through all games to find every player
for (const game of allGames) {
  if (game.teams?.home?.probablePitcher?.id) {
    allPlayerIds.add(game.teams.home.probablePitcher.id);
  }
  if (game.teams?.away?.probablePitcher?.id) {
    allPlayerIds.add(game.teams.away.probablePitcher.id);
  }
  if (game.lineups && Array.isArray(game.lineups.homePlayers)) {
    for (const player of game.lineups.homePlayers) {
      if (player.id) allPlayerIds.add(player.id);
    }
  }
  if (game.lineups && Array.isArray(game.lineups.awayPlayers)) {
    for (const player of game.lineups.awayPlayers) {
      if (player.id) allPlayerIds.add(player.id);
    }
  }
}

if (allPlayerIds.size === 0) {
  return [];
}

// We pass the original game data THROUGH this node by adding it to the output
const output = items[0].json;
output.playerIdsString = Array.from(allPlayerIds).join(',');
return [{ json: output }];

const allMatchupRows = [];

// Get player stats from the direct input of this node
const playerStatsData = items[0].json;

// Get the original game data by looking back at the previous node
const scheduleNode = $('3. Extract All Player IDs').first();
const originalScheduleData = scheduleNode.json;
const games = originalScheduleData.dates[0]?.games || [];

const playersWithStats = playerStatsData.people || [];

if (games.length === 0 || playersWithStats.length === 0) {
  return [];
}

const statsMap = new Map(playersWithStats.map(p => [p.id, p]));

for (const game of games) {

  const gameStartTime = game.gameDate || 'N/A';

  // --- Home Pitcher vs Away Batters ---
  const homePitcherId = game.teams?.home?.probablePitcher?.id;
  const awayLineup = game.lineups?.awayPlayers;

  if (homePitcherId && Array.isArray(awayLineup)) {
    const pitcherData = statsMap.get(homePitcherId);
    if (pitcherData) {
      for (const batter of awayLineup) {
        const batterData = statsMap.get(batter.id);
        if (batterData) {
          const pitcherStats = pitcherData.stats?.find(s => s.group?.displayName === 'pitching')?.splits[0]?.stat || {};
          const batterStats = batterData.stats?.find(s => s.group?.displayName === 'hitting')?.splits[0]?.stat || {};
          allMatchupRows.push({
            gameStartTime: gameStartTime,
            gameDate: game.officialDate || 'N/A',
            pitcherName: pitcherData.fullName || 'N/A',
            pitcherTeam: game.teams?.home?.team?.name || 'N/A',
            pitcherThrows: pitcherData.pitchHand?.description || 'N/A',
            pitcherERA: pitcherStats.era,
            pitcherSO: pitcherStats.strikeOuts,
            opponent: game.teams?.away?.team?.name || 'N/A',
            batterName: batterData.fullName || 'N/A',
            batterPosition: batter.primaryPosition?.abbreviation || 'N/A',
            batterBats: batterData.batSide?.description || 'N/A',
            batterAVG: batterStats.avg,
            batterOPS: batterStats.ops,
            batterHR: batterStats.homeRuns,
            batterHits: batterStats.hits,
            batterRBI: batterStats.rbi,
            pitcherId: pitcherData.id, // ADDED: Pitcher ID
            batterId: batterData.id,   // ADDED: Batter ID
          });
        }
      }
    }
  }

  // --- Away Pitcher vs Home Batters ---
  const awayPitcherId = game.teams?.away?.probablePitcher?.id;
  const homeLineup = game.lineups?.homePlayers;

  if (awayPitcherId && Array.isArray(homeLineup)) {
    const pitcherData = statsMap.get(awayPitcherId);
    if (pitcherData) {
      for (const batter of homeLineup) {
        const batterData = statsMap.get(batter.id);
        if (batterData) {
          const pitcherStats = pitcherData.stats?.find(s => s.group?.displayName === 'pitching')?.splits[0]?.stat || {};
          const batterStats = batterData.stats?.find(s => s.group?.displayName === 'hitting')?.splits[0]?.stat || {};
          allMatchupRows.push({
            gameStartTime: gameStartTime,
            gameDate: game.officialDate || 'N/A',
            pitcherName: pitcherData.fullName || 'N/A',
            pitcherTeam: game.teams?.away?.team?.name || 'N/A',
            pitcherThrows: pitcherData.pitchHand?.description || 'N/A',
            pitcherERA: pitcherStats.era,
            pitcherSO: pitcherStats.strikeOuts,
            opponent: game.teams?.home?.team?.name || 'N/A',
            batterName: batterData.fullName || 'N/A',
            batterPosition: batter.primaryPosition?.abbreviation || 'N/A',
            batterBats: batterData.batSide?.description || 'N/A',
            batterAVG: batterStats.avg,
            batterOPS: batterStats.ops,
            batterHR: batterStats.homeRuns,
            batterHits: batterStats.hits,
            batterRBI: batterStats.rbi,
            pitcherId: pitcherData.id, // ADDED: Pitcher ID
            batterId: batterData.id,   // ADDED: Batter ID
          });
        }
      }
    }
  }
}

return allMatchupRows.map(row => ({ json: row }));

// Convert to Eastern Time using proper timezone handling
function convertToEasternTime(isoString) {
	const date = new Date(isoString);
	return date.toLocaleTimeString('en-US', {
		hour: '2-digit',
		minute: '2-digit',
		hour12: true,
		timeZone: 'America/New_York'
	});
}

const HEADER_ORDER = [
	"gameDate",
	"gameStartTime",
	"pitcherId",
	"pitcherName",
	"pitcherTeam",
	"pitcherSO",
	"pitcherERA",
	"pitcherThrows",
	"batterBats",
	"opponent",
	"batterName",
	"batterAVG",
	"batterHR",
	"batterHits",
	"batterOPS",
	"batterRBI",
	"batterId",
	"batterPosition"
];

const allMatchups = items.map(item => item.json);

// Filter valid rows
const valid = allMatchups.filter(m => {
	const era = parseFloat(m.pitcherERA);
	const ops = parseFloat(m.batterOPS);
	return !isNaN(era) && era > 3.33 && !isNaN(ops) && m.gameStartTime;
});

// Parse and add helper fields
valid.forEach(m => {
	m.era = parseFloat(m.pitcherERA);
	m.ops = parseFloat(m.batterOPS);
	m.gameStartObj = new Date(m.gameStartTime);
});

// Step 1: Get top 9 unique pitchers with highest ERA
const seenPitchers = new Set();
const topPitchers = [];

valid
	.sort((a, b) => b.era - a.era)
	.forEach(m => {
		if (!seenPitchers.has(m.pitcherName)) {
			seenPitchers.add(m.pitcherName);
			topPitchers.push(m.pitcherName);
		}
	});

const top9 = topPitchers.slice(0, 9);

// Step 2: For each of these pitchers, get top 3 batters by OPS
const final = [];

top9.forEach(pitcherName => {
	const matchups = valid.filter(m => m.pitcherName === pitcherName);
	const seenBatters = new Set();
	const topBatters = [];

	matchups
		.sort((a, b) => b.ops - a.ops)
		.forEach(m => {
			if (!seenBatters.has(m.batterId)) {
				seenBatters.add(m.batterId);
				topBatters.push(m);
			}
		});

	final.push(...topBatters.slice(0, 3));
});

// Final sort by game start time
final.sort((a, b) => a.gameStartObj - b.gameStartObj);

// Format time and construct output with locked header order
const output = final.map(m => {
	m.gameStartTime = convertToEasternTime(m.gameStartObj.toISOString());
	const obj = {};
	HEADER_ORDER.forEach(key => obj[key] = m[key] ?? '');
	return { json: obj };
});

return output;

const orderedKeys = [
  "gameDate",
  "gameStartTime",
  "pitcherId",
  "pitcherName",
  "pitcherTeam",
  "pitcherSO",
  "pitcherERA",
  "pitcherThrows",
  "batterBats",
  "opponent",
  "batterName",
  "batterAVG",
  "batterHR",
  "batterHits",
  "batterOPS",
  "batterRBI",
  "batterId",
  "batterPosition"
];

// fields that should explicitly be numbers
const numericKeys = [
  "pitcherId",
  "pitcherSO",
  "pitcherERA",
  "batterAVG",
  "batterHR",
  "batterHits",
  "batterOPS",
  "batterRBI",
  "batterId"
];

// 🔷 Helper: format to hh:mm AM/PM ET
function toEasternTimeHHMM(value) {
  if (typeof value === 'string' && /^\d{1,2}:\d{2}/.test(value)) {
    return value.replace(/:00$/, ''); // clean up trailing :00 if it’s there
  }
  try {
    const utcDate = new Date(value);
    if (isNaN(utcDate.getTime())) return value;
    const options = {
      timeZone: 'America/New_York',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    };
    return new Intl.DateTimeFormat('en-US', options).format(utcDate);
  } catch {
    return value;
  }
}

// Build new list
const enriched = items.map(item => {
  const json = { ...item.json };

  if (json.gameStartTime) {
    json.gameStartTime = toEasternTimeHHMM(json.gameStartTime);
  }

  const output = {};
  for (const key of orderedKeys) {
    let val = json[key] ?? "";
    if (numericKeys.includes(key) && val !== "") {
      val = Number(val);

      // Round batterAVG and batterOPS to .000
      if (key === "batterAVG" || key === "batterOPS") {
        val = Number(val.toFixed(3));
      }
    }
    output[key] = val;
  }

  return { json: output };
});

// Sort enriched list
enriched.sort((a, b) => {
  const parseTime = (timeStr) => {
    const [time, meridian] = timeStr.split(' ');
    let [hours, minutes] = time.split(':').map(Number);
    if (meridian === 'PM' && hours !== 12) hours += 12;
    if (meridian === 'AM' && hours === 12) hours = 0;
    return hours * 60 + minutes; // total minutes since midnight
  };

  const aTime = parseTime(a.json.gameStartTime);
  const bTime = parseTime(b.json.gameStartTime);
  if (aTime < bTime) return -1;
  if (aTime > bTime) return 1;

  const aPitcher = Number(a.json.pitcherId) || 0;
  const bPitcher = Number(b.json.pitcherId) || 0;
  if (aPitcher < bPitcher) return -1;
  if (aPitcher > bPitcher) return 1;

  const aOpp = a.json.opponent || "";
  const bOpp = b.json.opponent || "";
  return aOpp.localeCompare(bOpp);
});


return enriched;

// This code is for an n8n Code node.
// It assumes the input from the preceding node (which now provides all 27 batters)
// allows 'items' to contain all batter records.

console.log("--- Code Node for Telegram Message Start ---");

// --- Input Processing (same as before to get all 27 batters) ---
let allBatterStats = [];

// This block handles both scenarios:
// 1. If "Run Once for All Items" is ON, 'items' contains all incoming records.
// 2. If an "Item Lists (Aggregate)" node precedes this, 'items[0].json' will be the array.
for (const item of items) {
  // Scenario A: Item contains a single object (e.g., from a direct data source outputting individual items)
  if (item && typeof item.json === 'object' && item.json !== null && !Array.isArray(item.json)) {
    allBatterStats.push(item.json);
  }
  // Scenario B: Item.json is an array (e.g., from an Item Lists aggregate node, or if a source directly outputs a single array)
  else if (item && Array.isArray(item.json)) {
    allBatterStats.push(...item.json);
  }
  // Fallback: If the item itself (not its .json) is the object, or other unexpected structures
  else if (typeof item === 'object' && item !== null && item.batterName && item.batterHits) {
      allBatterStats.push(item);
  } else {
      console.warn("Skipping an input item with an unrecognized structure:", JSON.stringify(item, null, 2));
  }
}

console.log("Total batter stats collected:", allBatterStats.length);

// Ensure batterHits is a number and filter out invalid entries.
const processedBatterStats = allBatterStats.map(batter => {
    const newBatter = { ...batter };
    newBatter.batterHits = typeof batter.batterHits === 'string'
                           ? parseFloat(batter.batterHits)
                           : batter.batterHits;
    if (isNaN(newBatter.batterHits)) {
        newBatter.batterHits = 0; // Default invalid numbers to 0
    }
    return newBatter;
}).filter(batter =>
    typeof batter === 'object' &&
    batter !== null &&
    typeof batter.batterHits === 'number' &&
    typeof batter.batterName === 'string' &&
    batter.batterName.trim() !== ''
);

console.log("Number of valid and processed batter stats:", processedBatterStats.length);

if (processedBatterStats.length === 0) {
  console.warn("No valid batter stats found. Returning empty message.");
  return [{ json: { text: "No batter stats available to display." } }];
}

// Sort the stats by batterHits in descending order.
processedBatterStats.sort((a, b) => b.batterOPS - a.batterOPS);

// Get the top 21 batters.
const top21Batters = processedBatterStats.slice(0, 21);

console.log("Number of top 21 batters selected:", top21Batters.length);

// --- Telegram Message Formatting ---

// Start the message with a title. Using Markdown for bold.
let telegramMessage = "⚾ **Top 21 Batters by OPS** ⚾\n\n";

// Add each batter to the message string.
top21Batters.forEach((batter, index) => {
    // Format each line using Markdown for name and hits.
    // Remember to escape any special Markdown characters in the data if necessary,
    // though typically names and numbers are safe.
    telegramMessage += `${index + 1}. **${batter.batterName}**: ${batter.batterOPS}\n`;
});

console.log("Generated Telegram message length:", telegramMessage.length);

// --- Return as a single n8n item for the Telegram node ---
// The Telegram node's 'Text' field will consume the 'text' property from this output.
return [{
  json: {
    message: telegramMessage // This is the single string containing the entire message
  }
}];
