return (() => {
 try {
   // Obtiene los datos
   let datos = $json.data;
   // Si los datos no son un array, intenta normalizar
   if (!Array.isArray(datos)) {
     if (datos && typeof datos === 'object') {
       // Si es un objeto √∫nico, lo pone dentro de un array
       datos = [datos];
     } else {
       // En caso contrario, devuelve un error
       return [{ mensaje: "Error: Los datos proporcionados no son v√°lidos para procesamiento." }];
     }
   }
   // Procesa los datos ahora normalizados
   const resumen = datos.map(item => {
     // Validaciones para campos individuales
     const nombreValido = typeof item.Nombre === 'string' ? item.Nombre.trim() : null;
     const isNombreInvalido = !nombreValido || nombreValido === "?" || nombreValido.toLowerCase() === "desconocido";
     const nombreUsuario = isNombreInvalido ? "Usuario no identificado" : nombreValido;
     const mensajeRespondido = item["Mensaje Respondido"]
       ? `Mensaje Respondido: ${item["Mensaje Respondido"]}`
       : "Mensaje Respondido: Ninguno";
     return `Nombre: ${nombreUsuario}, Mensaje: ${item.Mensaje || "Mensaje no disponible"}, ${mensajeRespondido}, Hora: ${item.Hora || "Hora no informada"}, Fecha: ${item.Data || "Fecha no informada"}`;
   }).join('\n');
   return [{ mensaje: resumen }];
 } catch (error) {
   return [{ mensaje: `Error al procesar los mensajes: ${error.message}` }];
 }
})();

