// 1. Extract LaunchDarkly flags array from input
const ldItems = $input.all()[0]?.json?.items || [];

// 2. Extract code flags from another input node named 'Code Flags'
const codeFlags = $('Detect flags').all().map(item => item.json.flag);

// 3. Filter dead flags from LD
const deadFlags = ldItems.filter(ldFlag => {
  const isUsedInCode = codeFlags.includes(ldFlag.key);

  if (isUsedInCode) return false;

  const prod = ldFlag.environments?.production || {};
  const test = ldFlag.environments?.test || {};

  const archivedEverywhere = prod.archived && test.archived;
  const offInProd = prod.on === false;

  return archivedEverywhere || offInProd;
});

// 4. Return as array
return deadFlags.map(flag => ({
  json: {
    deadFlag: flag.key,
    reason: flag.environments?.production?.archived
      ? 'Archived'
      : 'Off in Production'
  }
}));

const pattern = /(ENABLE_[A-Z0-9_]+)/g;
const flags = new Set();

for (const item of $input.all()) {
  const content = item.json.content || ''; // Assumes content was previously fetched
  let match;
  while ((match = pattern.exec(content)) !== null) {
    flags.add(match[1]);
  }
}

return Array.from(flags).map(flag => ({ json: { flag } }));
