// n8n Code Node - Update Form Dropdown Options
// First item = Workflow JSON, From second item = New Model IDs

try {
  // Get all input items
  const allInputs = $input.all();

  // First item is the workflow
  const workflowData = allInputs[0]?.json;

  // From second item onwards are the new model IDs
  const newOptions = [];

  // Iterate through all items from index 1
  for (let i = 1; i < allInputs.length; i++) {
    const item = allInputs[i];

    // Extract ID from the json object
    if (item?.json?.id) {
      newOptions.push(item.json.id);
    }
  }

  // Fallback if no IDs found
  if (newOptions.length === 0) {
    newOptions.push('No models available');
  }

  // Copy and modify workflow
  const result = JSON.parse(JSON.stringify(workflowData));

  // Go through workflow nodes
  if (result.nodes) {
    result.nodes.forEach(node => {
      if (node.type === 'n8n-nodes-base.formTrigger' && 
          node.parameters?.formFields?.values) {

        node.parameters.formFields.values.forEach(field => {
          if (field.fieldType === 'dropdown' && field.fieldOptions) {
            // Set new dropdown options
            field.fieldOptions.values = newOptions.map(option => ({ option }));
          }
        });
      }
    });
  }

  return result;

} catch (error) {
  // Error handling with debug info
  return {
    error: error.message,
    totalInputs: $input.all().length,
    sampleIds: $input.all().slice(1, 4).map(item => item?.json?.id),
    fallback: $input.first()?.json
  };
}
