// Get the first key-value from the "Get object name and value" node
// Replace 'Get_object_name_and_value' with the actual name of your node
const firstNodeData = $node["Get object name and value"].json;

// Get the previous node data (the one with multiple keys)
const previousNodeData = items[0].json;

// Create the output object
const output = {};

// Add the first key-value pair from the first node
// Assuming the node only has one key-value pair
const firstKey = Object.keys(firstNodeData)[0];
output[firstKey] = String(firstNodeData[firstKey]);

// Add all key-value pairs from the previous node, stringified
for (const [key, value] of Object.entries(previousNodeData)) {
    if (Array.isArray(value)) {
        output[key] = value.join(', ');
    } else if (typeof value === 'object' && value !== null) {
        output[key] = JSON.stringify(value);
    } else {
        output[key] = String(value);
    }
}

// Return the new item
return [{ json: output }];

