// Advanced contract analysis and scoring
const extracted = $node['PDF Vector - Extract All Data'].json.data;
const riskAnalysis = JSON.parse($node['PDF Vector - Risk Analysis'].json.answer);
const template = $node['Get Contract Template'].json;
const crmData = $node['Check Salesforce'].json;

// Calculate comprehensive risk score
let riskFactors = [];
let totalRiskScore = 0;

// Financial risk scoring
const contractValue = extracted.contractValue?.total || 0;
const annualRevenue = crmData.account?.annualRevenue || 1000000;
const dealSizeRisk = (contractValue / annualRevenue) > 0.1 ? 8 : 3;
riskFactors.push({category: 'Deal Size', score: dealSizeRisk});

// Check for missing standard clauses
const missingClauses = [];
if (!extracted.liability?.limitationOfLiability) {
  missingClauses.push('Limitation of Liability');
  riskFactors.push({category: 'Missing LoL', score: 9});
}
if (!extracted.termination?.forConvenience) {
  missingClauses.push('Termination for Convenience');
  riskFactors.push({category: 'No Exit Clause', score: 7});
}

// Payment term risks
if (extracted.paymentTerms?.schedule?.[0]?.dueDate) {
  const firstPayment = new Date(extracted.paymentTerms.schedule[0].dueDate);
  const daysToPay = (firstPayment - new Date()) / (1000 * 60 * 60 * 24);
  if (daysToPay > 60) {
    riskFactors.push({category: 'Extended Payment Terms', score: 6});
  }
}

// Auto-renewal risk
if (extracted.term?.autoRenewal && extracted.term?.renewalNotice < 30) {
  riskFactors.push({category: 'Short Renewal Notice', score: 7});
}

// Calculate weighted risk score
totalRiskScore = riskFactors.reduce((sum, risk) => sum + risk.score, 0) / riskFactors.length;

// Determine approval level
let approvalLevel = 'Manager';
if (totalRiskScore > 7 || contractValue > 1000000) approvalLevel = 'Director';
if (totalRiskScore > 8 || contractValue > 5000000) approvalLevel = 'VP';
if (missingClauses.includes('Limitation of Liability')) approvalLevel = 'Legal';

// Calculate key dates and alerts
const alerts = [];
const today = new Date();

// Contract expiration alert
if (extracted.term?.expirationDate) {
  const expDate = new Date(extracted.term.expirationDate);
  const daysToExpire = (expDate - today) / (1000 * 60 * 60 * 24);
  if (daysToExpire < 90) {
    alerts.push({
      type: 'Contract Expiration',
      date: extracted.term.expirationDate,
      daysRemaining: Math.floor(daysToExpire),
      severity: daysToExpire < 30 ? 'High' : 'Medium'
    });
  }
}

// Deliverable alerts
if (extracted.deliverables) {
  extracted.deliverables.forEach(deliverable => {
    const dueDate = new Date(deliverable.dueDate);
    const daysToDue = (dueDate - today) / (1000 * 60 * 60 * 24);
    if (daysToDue > 0 && daysToDue < 30) {
      alerts.push({
        type: 'Upcoming Deliverable',
        description: deliverable.description,
        date: deliverable.dueDate,
        daysRemaining: Math.floor(daysToDue),
        severity: daysToDue < 7 ? 'High' : 'Medium'
      });
    }
  });
}

return [{
  json: {
    contractAnalysis: {
      contractId: extracted.contractId || `AUTO-${Date.now()}`,
      type: extracted.contractType,
      parties: extracted.parties,
      value: contractValue,
      currency: extracted.contractValue?.currency || 'USD',
      effectiveDate: extracted.term?.effectiveDate,
      expirationDate: extracted.term?.expirationDate
    },
    riskAssessment: {
      overallScore: totalRiskScore,
      riskLevel: totalRiskScore > 7 ? 'High' : totalRiskScore > 4 ? 'Medium' : 'Low',
      riskFactors: riskFactors,
      missingClauses: missingClauses,
      aiRiskAnalysis: riskAnalysis
    },
    approvalRouting: {
      level: approvalLevel,
      reason: riskFactors.filter(r => r.score > 6).map(r => r.category).join(', ')
    },
    alerts: alerts,
    crmAlignment: {
      opportunityId: crmData.id,
      accountName: crmData.account?.name,
      dealSize: crmData.amount,
      stage: crmData.stage
    },
    metadata: {
      sourceChannel: $input.first().json.sourceChannel || 'Unknown',
      receivedDate: new Date().toISOString(),
      fileHash: $json.fileHash
    },
    fullExtraction: extracted
  }
}];

