const { TP, FP, FN } = $input.item.json.output;

return {
  f1Score: fbetaScore(TP.length, FP.length, FN.length)
};

function fbetaScore(tp, fp, fn, beta = 1.0) {
  const precision = tp + fp === 0 ? 0 : tp / (tp + fp);
  const recall = tp + fn === 0 ? 0 : tp / (tp + fn);

  if (precision === 0 && recall === 0) return 0.0;

  const betaSquared = beta * beta;
  const fbeta = (1 + betaSquared)
    * (precision * recall)
    / ((betaSquared * precision) + recall);

  return fbeta;
}

const {
  f1Score,
  similarityScore,
} = $input.item.json;

const weights = [0.75, 0.25];

return {
  score: weightedAverage([f1Score, similarityScore], weights)
};

function weightedAverage(values, weights) {
  const weightedSum = values.reduce((sum, val, i) => sum + val * weights[i], 0);
  const totalWeight = weights.reduce((sum, w) => sum + w, 0);
  return weightedSum / totalWeight;
}

const { answer, groundTruth = [] } = $input.item.json;

const scores = await Promise.all(groundTruth.map(truth =>
  cosineSimilarity(answer, truth.data)
));

const scoreAvg = scores.reduce((acc,score) => acc + score, 0) / scores.length;

return { json: { similarityScore: scoreAvg } }

function cosineSimilarity(a, b) {  
  let dotProduct = normA = normB = 0;
  for (let i = 0; i < a.length; i++) {
    dotProduct += a[i] * b[i];
    normA += a[i] ** 2;
    normB += b[i] ** 2;
  }
  return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
}
